css:
@-moz-document domain("wiki.ubuntuusers.de") {
/* #################### General Body #################### */
body{
    background-color: #1F2933;
    color:white;
}

body form.search .search_submit{
	color:white;	
}


body a:link{
	color: #FFB300;
}

body a:visited{
	color: #FF6F00;
}

body blockquote *{
	color:white;
}
}

@-moz-document domain("wiki.ubuntuusers.de") {
/* Main */
div.bash pre.notranslate{
	background-color: black;
}

section pre.notranslate{
	background-color: #616E7C;
}

div.toc{
	background-color: #323F4B;
}

body .tabbar li a{
	color:white;	
}
}

@-moz-document domain("wiki.ubuntuusers.de") {
/* Sidebar */

#main{
	background-color: #323F4B;
	padding: 10px;
}

#sidebar *{
	color:white;
}

#sidebar a{
	color: #FFB300;
}
}

@-moz-document domain("wiki.ubuntuusers.de") {
/* Boxes */


div.box.warning{
	background-color: #C62828;
}

div.box.notice{
	background-color: #7F8790;
}

div.box.tested_for{
	background-color: #00897B;	
}

div.box.knowledge{
	background-color: #FFA000;	
}

div.box.knowledge a:link{
	color: blue;
}

div.box.knowledge a:visited{
	color: blue;
}
}

@-moz-document domain("wiki.ubuntuusers.de") {
/* Tables */
table tbody{
	background-color: #1F2933;
}

table.zebra_start3 tbody tr:nth-child(2n+4){
	background-color: #3E4C59;
}



tr[style*="background-color"]{
	color:black;	
}

table.syntaxtable{
	color:black;
}
}
