body {
    background-image:none;
    background-color: #00161c;
    font-size:18px
}
#left-column {
    margin-top: 20px;
    width: auto;
    position: relative;
    right: auto;
}
.middle_content_game {
    border: none;
    border-radius: 5px;
    background-color: #00161c;
