css:
@-moz-document url-prefix("https://www.3dmgame.com/") {
body {
    font-family:"微软雅黑", 'Microsoft YaHei', 'Arial';
}
.header_wrap .header_center .hedtop {
    padding-top:5px;
}
.header_wrap .header_center {
    height:auto;
}
.header_wrap .header_center .header .search {
    left:500px;
    top:-25px;
}
.header_wrap .header_center .header {
    height:75px
}
.header_wrap .header_center .header .logo {
    margin-top:10px;
    width:220px
}
.Indexbox2 .Indexbox2-2 .bttile .lab::after {
    background:none;
}
.header_wrap .header_center .header .search {
    border:1px solid #ff7c26;
    border-radius:5px;
    height:30px;
}
.header_wrap .header_center .header .search .bj, .header_wrap .header_center .header .search .bj::after {
    background:none!important;
}
.header_wrap .header_center .header .search input {
    height:30px;
    line-height:30px;
    margin:0 10px;
}
.header_wrap .header_center .header .search .but i {
    margin:0;
    height:30px;
    width:30px;
    background-position:-412px -199px!important;
}
.header_wrap .header_center .header .addapp {
    display:none;
}
}
