css:
@-moz-document domain(vk.com) {
	/* your code goes here */

[dir] body {
    background: white;
    margin: 0;
    padding: 0;
}

.MenuList__itemText {
    font-size: 13px;
}

.VideoGrid {
    grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
}
a.VideoCard__title {
    font-size: 13px;
}

.VideoCard {
    position: relative;
    padding: 10px;
}

.MenuList__itemIcon {
    width: 20px;
    height: 20px;
}

[dir] .MenuList__item {
    border-radius: 4px;
    padding: 0 30px;
    width: 163px;
    height: 30px;
    margin-left:10px;
}

[dir] .PlaylistPanel__item {
    border-radius: 4px;
    padding: 0 20px;
}

[dir] .PlaylistPanel__list {
    padding: 0 30px;
}

.PlaylistPanel__itemText {
    font-size: 13px;
}
}
