css:
@-moz-document domain("en.wikipedia.org") {
/*   -=< Darker Wikipedia by Zigboom Designs >=-   */

body {
    background-color: #181818;
}
body * {
    background-color: #181818 !important;
    border-color: #333 !important;
    color: #aaa;
}

.mw-footer li {
    color: #aaa;
}

.mw-parser-output #mp-left .mp-h2, .mw-parser-output #mp-right .mp-h2, .mw-parser-output #mp-tfp-h2, .mw-parser-output #mp-other-lower .mp-h2, .mw-headline {
    background: #181818 !important;
    border: none !important;
}

.mw-parser-output #mp-upper .mid-table {
    border-color: transparent !important;
}

.mw-wiki-logo {
    background-color: transparent !important;
    filter: invert(0.6);
}

a, .vector-menu-tabs .selected span, .wb-langlinks-link > a:link, .wb-langlinks-link > a:visited, .vector-search-box-input {
    color: #ddd !important;
    transition: color 0.4s;
}

a:hover {
    color: #eee !important;
    text-decoration: none !important;
    text-shadow: 0 0 4px rgba(255, 255, 255, 0.4) !important;
}

.vector-menu-tabs, .vector-menu-tabs a, #mw-head .vector-menu-dropdown h3 {
    background-image: linear-gradient(to bottom,#282828 0,#333 100%);
}

.vector-menu-portal h3 {
    background-image: linear-gradient(to right,#282828 0,#333 33%,#444 66%,#282828 100%) !important;
}

.vector-menu-tabs, .vector-menu-tabs a, #mw-head .vector-menu-dropdown h3 {
    border-bottom: 1px solid #444;
}

.mw-parser-output a.external, .link-https, .wb-langlinks-link > a:link, .wb-langlinks-link > a:visited {
    filter: grayscale(1);
}

.searchButton[name='go'] {
    background-color: transparent !important;
    filter: invert(1);
}
    
#footer-icons li {
    filter: grayscale(1) invert(1);
    background-color: transparent !important;
}

#footer-icons li img {
    background-color: transparent !important;
    border: 1px solid #aaa !important;
    border-radius: 5px;
}

img.mwe-math-fallback-image-inline {
background: none !important;
filter: invert(.9);
}

/* The End */
}

@-moz-document url-prefix("https://www.wikipedia.org/") {
html {
    filter: grayscale(1) invert(0.9);
}
/* The End */
}
