css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ficwad.com") {

#main, #header #primary span, #header #primary a.current, #header #primary a.current:hover, #header #primary a:hover {

background-color: black !important;
color: white !important;

}

body, #maincontent, #boxes div, #alert, #storylist li, .reviewslist li, #set_of_links, #set_of_links a:hover, #contents {
background-color: #333333 !important;
}

#boxes span.action, #boxes h4, .review h4, .review .options, #header #primary a, #story blockquote, #storylist li .score .score_adjective {
 background-color: #222222 !important;
}
/* MESSAGE */
#message {
background-color: #000066 !important;
color: white !important;
border: 1px solid #FFFFFF !important;
}

/* Buttons */
#buttonboxes li a, #buttonboxes .action_link button {
 background-color: #333333 !important;
 border: 1px solid white !important;
 color: white !important;
}

#buttonboxes li a.active {
 background-color: #000000 !important;
}
#buttonboxes li a.active:hover {
 background-color: #222222 !important;
}

#buttonboxes li a:hover, #buttonboxes .action_link button:hover {
 background-color: #444444 !important;
}

input, textarea, select, form .static {
background-color: black !important;
color: white !important;
}

#storylist li .score, .adjectivelist li .score {
border: 1px solid white !important;
}

/* SPECIAL STORIES */
#storylist li.score-1, #storylist li.score-1 *{
opacity:0.5;
background-color: #222222 !important;
}

#storylist li.blocked, #storylist li.blocked *{
opacity:0.7 !important;
background-color: #403333 !important;
}

#storylist li.score5, .adjectivelist li.score5 .score, #storylist li.score5 blockquote, #storylist li.score5 .score * {
background-color: #003300 !important;
}
#storylist blockquote {
background-color: #222222 !important;
}

a {
  color: #ff0 !important;
  text-decoration: none !important;
  border: none !important;
}
a:visited { color: #f00 !important; }
a:hover, a:visited:hover { color: #ffa900 !important; }


select, input, textarea {
  background: #111 !important;
  color: #fff !important;
  border: 1px solid #ccc !important;
  -moz-appearance: none !important;
}

select:hover,select:focus,input:hover,input:focus,textarea:hover,textarea:focus { 
border-color: #fff !important; 
}

.req, .req a {
color: #666666 !important;
}

.req a:hover{
color: #555555 !important;
}

/* Forum */
#forum th, #forum h3 {
color: #FFFFFF !important;
}
#forum .dark {
background-color: #222222 !important;
color: #FFFFFF !important;
}
#forum .color {
background-color: #555555 !important;
color: #FFFFFF !important;
}


/* Boxes */
#boxes h4 {
border-bottom: none !important;
}
#alert, .categories li a, .footer, .review li, .review .rating, .review h4, .review .options {
border: 1px solid #CCCCCC !important;
}
.categories li ul.categories a { 
border: none !important;
}

/* HEADER */
#slogan {
color: #FFFFFF !important;
}
#header #secondary li span, #header #secondary li a.current, #header #secondary li a, #header #secondary li a:hover {
border: 0px none !important;
border-right: 1px solid #FFFFFF !important;
background: black !important;
}
#header #secondary li a:hover {
background: #222222 !important;
}
#header #secondary li a.current {
background: #333333 !important;
}
#header #secondary li a.current:hover {
background: #444444 !important;
}
}
