css:
@-moz-document domain('login.sae.edu') {
    :root {
        --dtheme-color-primary: #efefef;
        --dtheme-bg-primary: #0d1117;
        --dtheme-bg-secondary: #151a21;
        --dtheme-color-sae-orange: #ff6900;
    }

    body,
    .content,
    .header {
        background: var(--dtheme-bg-primary);
        color: var(--dtheme-color-primary);
    }

    a#ot-sdk-btn.ot-sdk-show-settings,
    a.link-light,
    a.link-light:hover,
    a.link-light:focus,
    a.link-light:active,
    a.link-light:link {
        color: var(--dtheme-color-sae-orange) !important;
    }

    a,
    a:hover {
        color: var(--dtheme-color-sae-orange);
    }

    .content {
        padding: 3rem 1rem;
    }

    input:-webkit-autofill,
    input:-webkit-autofill:hover,
    input:-webkit-autofill:focus,
    input:-webkit-autofill:active {
        -webkit-box-shadow: 0 0 0 30px var(--dtheme-bg-secondary) inset !important;
    }

    input:-webkit-autofill {
        -webkit-text-fill-color: var(--dtheme-color-primary);
    }

    .form-control {
        background: var(--dtheme-bg-secondary);
        border: 1px var(--dtheme-bg-secondary) solid;
        color: var(--dtheme-color-primary);
    }

    .form-control:hover {
        border-color: var(--dtheme-color-primary);
    }

    .form-control:focus {
        border-color: var(--dtheme-color-sae-orange);
        background: var(--dtheme-bg-secondary);
        color: var(--dtheme-color-primary);
    }

    .form-control:disabled {
        background: var(--dtheme-bg-primary);
        border-color: var(--dtheme-bg-primary);
    }

    .btn-primary {
        background: var(--dtheme-bg-secondary);
        border: 1px var(--dtheme-bg-secondary) solid;
    }

    .btn-primary:hover {
        border-color: var(--dtheme-color-primary);
        background: var(--dtheme-bg-secondary);
    }

    @media(max-width: 768px) {
        .logo {
            display: block;
            margin: 0px auto;
        }

        .content {
            padding: 0px 1rem;
        }

        .col-12.offset-md-1.text-light {
            text-align: center;
        }
    }
}
