/*--全局*/
@keyframes example {
    0% {
        background: linear-gradient(60deg, #f00 0%, #f70 14%, #ff0 28%, #4f0 42%, #0ff 56%, #07f 70%, #70f 84%)
    }
    14% {
        background: linear-gradient(60deg, #70f 0%, #f00 14%, #f70 28%, #ff0 42%, #4f0 56%, #0ff 70%, #07f 84%)
    }
    28% {
        background: linear-gradient(60deg, #07f 0%, #70f 14%, #f00 28%, #f70 42%, #ff0 56%, #4f0 70%, #0ff 84%)
    }
    42% {
        background: linear-gradient(60deg, #0ff 0%, #07f 14%, #70f 28%, #f00 42%, #f70 56%, #ff0 70%, #4f0 84%)
    }
    56% {
        background: linear-gradient(60deg, #4f0 0%, #0ff 14%, #07f 28%, #70f 42%, #f00 56%, #f70 70%, #ff0 84%)
    }
    70% {
        background: linear-gradient(60deg, #ff0 0%, #4f0 14%, #0ff 28%, #07f 42%, #70f 56%, #f00 70%, #f70 84%)
    }
    84% {
        background: linear-gradient(60deg, #f70 0%, #ff0 14%, #4f0 28%, #0ff 42%, #07f 56%, #70f 70%, #f00 84%)
    }
    100% {
        background: linear-gradient(60deg, #f00 0%, #f70 14%, #ff0 28%, #4f0 42%, #0ff 56%, #07f 70%, #70f 84%)
    }
}
@keyframes exlg1 {
    from {
        top: 150px;
        background-color: #fff0;
        color: #fff0
    }
    to {
        top: 50px;
    }
}
@keyframes popout1 {
    from {
        left: 0px;
        background-color: #fff0;
        color: #fff0
    }
    to {
        left: 50px;
    }
}
html,
body {
    background-color: #f4f5f7 !important;
}
html,
body {
    font-family: 'open sans' !important;
}
body {
    overflow-x: hidden;
}
h1,
h2,
h3,
h4,
h5,
h6 {
    font-weight: 600 !important;
}
* > h2:first-child {
    margin-bottom: 1.6rem;
}

/*--顶栏*/
.lg-header,
.lg-header-list > li {
    background-color: #000000;
    border-color: #000000;
}
.lg-header {
    box-shadow: 0 5px 15px rgba(50, 50, 93, .1), 0 5px 8px rgba(0, 0, 0, .07)!important;
}
    

    
/*--代码字体*/
code,
.hljs {
    font-family: consolas, monospace, "Courier New";
}
/*--加载进度条处理*/
#nprogress .nprogress-peg {
    box-shadow: none !important;
}
#nprogress .nprogress-bar {
    position: fixed;
    top: 0 !important;
    background: #5eb95e;
}
/*--选中内容的样式*/
*::selection {
    background-color: #cce2ff;
}
*::-moz-selection {
    background-color: #cce2ff;
}
*::-webkit-selection {
    background-color: #cce2ff;
}
/*--滚动条*/
::-webkit-scrollbar {
    width: 6px;
    height: 10px;
    background-color: rgba(0, 0, 0, 0);
}
::-webkit-scrollbar-track {
    background-color: rgba(0, 0, 0, 0.05);
}
::-webkit-scrollbar-thumb {
    background-color: rgba(0, 0, 0, 0.2);
}
::-webkit-scrollbar-thumb:hover {
    background-color: #5e72e4;
}
/*--超链接*/
a {
    transition: all .15s;
    color: #00b9b9ed;
}
/*--颜色定义*/
.lg-fg-green {
    color: #2dce89 !important;
}
.lg-bg-red {
    background-color: #fb6340;
}
.lg-bg-orange {
    background-color: #ff9d09;
}
.lg-bg-purple {
    background-color: #8e44ad;
}
.lg-bg-green {
    background-color: #2dce89;
}
.lg-bg-bluedark {
    background-color: #001277;
}
/*--动画定义*/
@keyframes fadeInLeft {
    from {
        opacity: 0;
        transform: translateX(-35px);
    }
    to {
        opacity: 1;
        transform: translateX(0);
    }
}
@keyframes fade {
    from {
        opacity: 0;
    }
    to {
        opacity: 1;
    }
}
/*--侧栏*/
#app > .lfe-body {
    background: #fafafa !important/*linear-gradient(30deg,#7795f8 15%,#6772e5 70%,#555abf 94%)*/;
    box-shadow: 4px 5px 15px rgba(50, 50, 93, .1), 2px 5px 8px rgba(0, 0, 0, .07)!important;
}
#app > .lfe-body > a {
    color: #5e72e4 !important;
    height: 3.7em;
    margin-top: 3px;
    padding: 0 !important;
}
#app > .lfe-body > a:first-of-type {
    margin-top: 10px !important;
}
#app > .lfe-body > a > .icon {
    line-height: 2.4em;
    font-size: 18px;
    width: 100%;
    margin: 0 !important;
    transition: all 0.3s ease;
}
#app > .lfe-body > a > .text {
    opacity: 0;
    margin-top: -1em;
    display: block;
    transition: all 0.3s ease;
}
#app > .lfe-body > a:hover > .icon {
    line-height: 1.5em;
    font-size: 17px;
}
#app > .lfe-body > a:hover > .text {
    opacity: 1;
    margin-top: 0.2em;
}
#app > .lfe-body > div:first-of-type {
    background-color: #5e72e4 !important;
}
#app > .lfe-body > .popup-button {
    background-color: transparent !important;
    margin-top: 8px;
    color: #5e72e4 !important;
}
    

/*-侧栏Hover动画*/
.lg-sidebar-list li[class^="lg-nav"] a span[class^="am-icon"]:after {
    content: "";
    background: #5e72e4;
    position: absolute;
    left: 50%;
    right: 50%;
    bottom: 8px;
    height: 2px;
    width: 0px;
    border-radius: 10px;
    transition: all .15s ease;
}
.lg-sidebar-list li[class^="lg-nav"] a:hover span[class^="am-icon"]:after {
    left: 40%;
    right: 40%;
    width: 20%;
}
/*--输入框*/
.am-input-group,
.input-group {
    transition: box-shadow .15s ease;
}
.am-input-group {
    border-radius: .5rem !important;
}
.am-form-field,
.input-group > .input-wrap > input,
.input-wrap > input {
    transition: box-shadow .15s ease;
    border: none !important;
    border-radius: 4px !important;
    box-shadow: 0 1px 3px rgba(50, 50, 93, .15), 0 1px 0 rgba(0, 0, 0, .02);
    min-height: 25px !important;
}
.am-form-field:focus,
.input-group > .input-wrap > input:focus,
.input-wrap > input:focus {
    box-shadow: 0 4px 6px rgba(50, 50, 93, .08), 0 1px 3px rgba(0, 0, 0, .05) !important;
}
.am-form-field,
.input-group > button {
    transition: box-shadow .15s ease;
    border: none;
    border-radius: 4px !important;
    box-shadow: 0 1px 3px rgba(50, 50, 93, .15), 0 1px 0 rgba(0, 0, 0, .02);
}
.input-group > button {
    margin-left: 3px;
}
.am-form-field,
.input-group > button:hover {
    box-shadow: 0 4px 6px rgba(50, 50, 93, .08), 0 1px 3px rgba(0, 0, 0, .05) !important;
}
.am-input-group-sm > .am-form-field,
.am-input-group-sm > .am-input-group-btn > .am-btn,
.am-input-group-sm > .am-input-group-label {
    font-size: 1.35rem !important;
}
.am-input-group-label {
    background: transparent;
    border: none;
}
.am-input-group-danger,
.am-input-group-primary {
    box-shadow: none;
}
.am-input-group-danger > .am-input-group-label,
.am-input-group-primary > .am-input-group-label {
    border-radius: .5rem !important;
    outline: none !important;
    letter-spacing: .05em;
    transition: all .15s ease;
    box-shadow: 0 4px 6px rgba(50, 50, 93, .11), 0 1px 3px rgba(0, 0, 0, .08);
    color: #fff !important;
    margin-bottom: 6px;
    height: 33.4px;
}
.am-input-group-danger > .am-input-group-label:hover,
.am-input-group-primary > .am-input-group-label:hover {
    box-shadow: 0 7px 14px rgba(50, 50, 93, .1), 0 3px 6px rgba(0, 0, 0, .08);
}
.am-input-group-danger > .am-input-group-label:active,
.am-input-group-primary > .am-input-group-label:active {
    box-shadow: none;
}
.am-input-group-danger > .am-input-group-label {
    border-color: #f5365c;
    background-color: #f5365c;
    /*#ec0c38*/
}
.am-input-group-primary > .am-input-group-label {
    border-color: #5e72e4;
    background-color: #5e72e4;
    border-radius: .5rem !important;
}
textarea {
    box-shadow: 0 1px 3px rgba(50, 50, 93, .15), 0 1px 0 rgba(0, 0, 0, .02);
    border: none !important;
}
textarea:focus {
    box-shadow: 0 4px 6px rgba(50, 50, 93, .08), 0 1px 3px rgba(0, 0, 0, .05) !important;
}
.lg-input-title {
    margin-bottom: 5px;
    border-radius: 5px;
    border-color: #ddd;
}
/*-特判顶部工具条搜索*/
#topbar-search-text {
    border-radius: .5rem 0 0 .5em !important;
}
#topbar-search {
    border-radius: 0 .5rem .5em 0 !important;
    border-color: #5e72e4;
    background-color: #5e72e4;
}
/*--卡片*/
.lg-article,
.lg-summary,
.lg-article-sub,
.card {
    transition: all .15s ease;
    box-shadow: 0 5px 15px rgba(50, 50, 93, .1), 0 5px 8px rgba(0, 0, 0, .07)!important;
    word-wrap: break-word;
    border-radius: .99rem;
    background-color: #fcfcfc !important;
    overflow: visible !important;
}
.lg-article:hover,
.lg-summary:hover,
.lg-article-sub:hover,
.card:hover {
    box-shadow: 0 10px 30px rgba(50, 50, 93, .1), 0 5px 15px rgba(0, 0, 0, .07)!important;
}
.card {
    line-height: 1.8;
}
/*--按钮*/
.am-btn {
    outline: none !important;
    border: none !important;
    letter-spacing: .05em;
    padding: 8px 18px;
    transition: all .15s ease;
    box-shadow: 0 4px 6px rgba(50, 50, 93, .11), 0 1px 3px rgba(0, 0, 0, .08);
    color: #fff !important;
    margin-bottom: 6px;
}
.am-dropdown > .am-btn {
    color: inherit !important;
}
button[type='button'] {
    outline: none !important;
    border: none !important;
    box-shadow: 0 4px 6px rgba(50, 50, 93, .11), 0 1px 3px rgba(0, 0, 0, .08);
    transition: all .15s ease;
    margin-bottom: 6px;
    padding: 8px 18px;
}
.am-btn.am-disabled,
.am-btn[disabled],
fieldset[disabled] .am-btn,
button[type='button'][disabled],
button.disabled[type='button'] {
    box-shadow: none;
    opacity: .65;
}
.am-btn:hover,
button[type='button']:hover {
    box-shadow: 0 7px 14px rgba(50, 50, 93, .1), 0 3px 6px rgba(0, 0, 0, .08);
    transform: translateY(-1px);
}
.am-btn:active,
button[type='button']:active {
    box-shadow: none;
}
.am-btn-danger,
.am-btn-danger:hover,
.am-btn-danger:active,
.am-btn-danger:focus,
.am-btn-danger:visited {
    border-color: #f5365c;
    background-color: #f5365c;
    /*#ec0c38*/
}
.am-btn-primary,
.am-btn-primary:hover,
.am-btn-primary:active,
.am-btn-primary:focus,
a.am-btn-primary:visited {
    border-color: #7589ff;
    background-color: #7589ff;
}
.am-btn-success,
.am-btn-success:hover,
.am-btn-success:active,
.am-btn-success:focus,
a.am-btn-success:visited {
    border-color: #2dce89;
    background-color: #2dce89;
    border-radius: 99px;
}

.am-btn-warning,
.am-btn-warning:hover,
.am-btn-warning:active,
.am-btn-warning:focus,
a.am-btn-warning:visited {
    border-color: #fb6340;
    background-color: #fb6340;
}
/*-复制按钮*/
.copy-btn {
    border-color: #5e72e4!important;
    background-color: #5e72e4 !important;
    color: #fff !important;
    margin-top: 3px;
}
/*--Tooltip*/
.tip-twitter {
    background: #172b4d;
    color: #fff;
    border-radius: 50px;
    box-shadow: 0 4px 6px rgba(50, 50, 93, .11), 0 1px 3px rgba(0, 0, 0, .08);
}
.tip-twitter > div {
    font-weight: unset !important;
    font-size: 13px !important;
    margin-left: 5px;
    margin-right: 5px;
}
/*--标签*/
.am-badge {
    border-radius: 50px !important;
    padding-left: 10px;
    padding-right: 10px;
    padding-top: 4px;
    padding-bottom: 4px;
    transition: all .15s;
}
.tag span.lfe-caption,
.difficulty span.lfe-caption {
    border-radius:70px !important;
    padding-left: 10px;
    padding-right: 10px;
    padding-top: 1px;
    padding-bottom: 1px;
    transition: all .15s;
}
.am-badge:hover,
.tag span.lfe-caption:hover,
.difficulty span.lfe-caption:hover {
    transform: translateY(-1px);
    box-shadow: 0 4px 6px rgba(50, 50, 93, .11), 0 1px 3px rgba(0, 0, 0, .08);
}
.am-badge,
.lfe-caption[slot='difficulty'],
.lfe-caption.tag,
.tags-wrap {
    overflow: visible !important;
}
.am-badge.lg-bg-red {
    background-color: #f80031;
}
.am-badge.lg-bg-orange {
    background-color: #ff471d;
}
.am-badge.lg-bg-yellow {
    background-color: #ff9d09;
}
.am-badge.lg-bg-green {
    background-color: #1aae6f;
}
.am-badge.lg-bg-bluelight {
    background-color: #03acca;
}
.am-badge.lg-bg-purple {
    background-color: #8e44ad;
}
.am-badge.lg-bg-bluedark {
    background-color: #001277;
}
.am-badge.am-badge-warning {
    background-color: #fb6340;
}
/*--菜单*/
.am-dropdown-content {
    padding: 1.5rem;
    border-radius: .25rem;
    background: #fff;
    width: 300px;
    border: none;
    margin-top: 5px;
