}
/*-通过率条 tooltip*/
.rate-wrapper .rate-popup {
    transition: all .25s ease !important;
    display: block !important;
    border-radius: 3px;
    box-shadow: 0 4px 6px rgba(50, 50, 93, .11), 0 1px 3px rgba(0, 0, 0, .08) !important;
    min-width: 4em;
    min-height: 1em;
    width: 4em;
    height: 1em;
    padding: 0 !important;
    margin-bottom: -15px;
    overflow: hidden !important;
    opacity: 0;
    color: transparent;
    background: transparent;
    pointer-events: none;
}
.rate-wrapper:hover .rate-popup {
    border-radius: 30px;
    margin-bottom: 10px;
    min-width: 10em;
    min-height: 1em;
    width: unset;
    height: unset;
    padding: 5px 18px !important;
    opacity: .8;
    color: #fff;
    background: #172b4d;
}
/*--页码*/
.am-pagination,
.page-area {
    padding-top: 20px;
    padding-bottom: 20px;
    width: max-content;
    width: -moz-max-content;
    margin-left: auto;
    margin-right: auto;
    border-top: none !important;
}
.am-pagination > li > a,
.page-area .page-bar > button {
    outline: none !important;
    cursor: pointer;
    line-height: 1.25;
    position: relative;
    display: block;
    margin-left: -.0625rem;
    padding: .5rem .75rem;
    color: #8898aa;
    border: .0625rem solid #dee2e6;
    background-color: #fff;
    font-size: 15px;
    font-weight: unset;
    display: flex;
    min-width: 36px;
    height: 36px;
    margin: 0 3px;
    border-radius: 100px!important;
    align-items: center;
    justify-content: center;
    transition: all .3s;
}
.page-area .page-bar > button:hover {
    background-color: #eee;
}
.am-pagination > .am-active > a,
.page-area .page-bar > button[style*='background'] {
    color: #fff;
    border-color: #fff0 !important;
    background-color: #4300ff !important;
    box-shadow: 0 7px 14px rgba(50, 50, 93, .1), 0 3px 6px rgba(0, 0, 0, .08);
}
.am-pagination > .am-active > a:hover {
    color: #fff;
    border-color: #fff0 !important;
    background-color: #4300ff !important;
}
/*-针对单独页码卡片特殊适配消除卡片*/
.lg-article .pagination-centered:before {
    content: "";
    background: #f4f5f7;
    width: calc(100% + 35px);
    height: calc(100% + 40px);
    position: absolute;
    left: -15px;
    top: -10px;
}
/*--评测详情色块*/
.lg-record-tile {
    transition: all .15s ease;
    box-shadow: 0 4px 6px rgba(50, 50, 93, .11), 0 1px 3px rgba(0, 0, 0, .08);
    border-radius: 3px;
}
.lg-record-tile:hover {
    transition: all .15s ease;
    transform: translateY(-5px);
    box-shadow: 0 10px 30px rgba(50, 50, 93, .1), 0 5px 15px rgba(0, 0, 0, .07);
}
/*--评测详情色块(新版)*/
.test-case-wrap > .wrapper {
    width: 100px;
    height: 100px;
    margin-right: 16px;
    margin-bottom: 12px;
}
.test-case-wrap {
    margin-top: 25px;
}
.test-case-wrap > .wrapper .test-case {
    width: 100px;
    height: 100px;
    transition: all .2s ease;
    box-shadow: 0 4px 6px rgba(50, 50, 93, .11), 0 1px 3px rgba(0, 0, 0, .08);
    border-radius: 3px;
    padding: 3px;
}
.test-case-wrap > .wrapper .test-case:hover {
    transform: translateY(-5px);
    box-shadow: 0 10px 30px rgba(50, 50, 93, .1), 0 5px 15px rgba(0, 0, 0, .07);
}
.test-case-wrap > .wrapper .test-case .info {
    width: calc(100% - 10px);
    line-height: 15px;
    font-size: 10px;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}
.test-case-wrap > .wrapper .test-case:hover .info {
    white-space: normal;
}
.test-case-wrap > .wrapper .test-case .id {
    margin-left: 3px;
}
.test-case-wrap > .wrapper .test-case .status {
    font-weight: bold;
    font-size: 22px;
    margin-bottom: 5px;
}
.test-case-wrap > .wrapper .message {
    transition: all .25s ease !important;
    display: block !important;
    border-radius: 3px;
    box-shadow: 0 4px 6px rgba(50, 50, 93, .11), 0 1px 3px rgba(0, 0, 0, .08);
    min-width: 105px;
    min-height: 105px;
    width: 105px;
    padding: 0;
    margin-top: 40px;
    opacity: 0;
    color: transparent;
    background: transparent;
    pointer-events: none;
}
.test-case-wrap > .wrapper:hover .message {
    border-radius: 30px;
    margin-top: -30px;
    min-width: 12em;
    min-height: 1em;
    width: unset;
    height: unset;
    padding: 5px 18px;
    opacity: .8;
    color: #fff;
    background: #172b4d;
}
.test-case-wrap > .wrapper .test-case[style*='rgb(112, 173, 71)'] {
    background-color: #2dce89 !important;
}
.test-case-wrap > .wrapper .test-case[style*='rgb(231, 76, 60)'] {
    background-color: #fb6340 !important;
}
.test-case-wrap > .wrapper .test-case[style*='rgb(157, 61, 207)'] {
    background-color: #8e44ad !important;
}
.test-case-wrap > .wrapper .test-case[style*='rgb(5, 34, 66)'] {
    background-color: #001277 !important;
}
.test-case-wrap > .wrapper .test-case[style*='rgb(14, 29, 105)'] {
    background-color: #34495e !important;
}
.test-case-wrap > .wrapper .test-case[style*='background: rgb(20, 85, 143);'] {
    background-color: #3498db !important;
}
