css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("iichan.hk") {
html, body {
  color:#000000 !important;
  background:#EEEEEE url('http://i42.fastpic.ru/big/2012/0909/97/59b707b3cc2cdd2896baf53904409a97.png') top center repeat-x !important;
}
.theader, .passvalid {
  background:#DDDDDD !important;
  text-align:center !important;
  padding:2px !important;
  color:#000000 !important;
  clear: both !important;
  font-weight: bold !important;
  margin-bottom: .5em !important;
  border: solid 1px #CCCCCC !important;
  -moz-border-radius: 5px !important;
}
.theader {
  margin-top: 5px !important;
}
* {
  font-family: Cambria, "Trebuchet MS", Tahoma, Verdana, Arial, sans-serif !important;
  font-size: 12pt !important;
}
input, textarea {
  background-color: #EEEEEE !important;
  border: 1px solid #CCCCCC !important;
  background-image:none !important;
}
a {
  background:none !important;
  color:#044983 !important;
}
a:visited {
  background:none !important;
  color:#044983 !important;
}
a:hover {
  color:#0066ff !important;
  background:none !important;
}
a.quotelink {
  background:inherit !important;
  color:#0066ff !important;
}
.logo, .logo a {
  clear:both !important;
  text-align:center !important;
  background:none !important;
  font-size:26px !important;
  font-weight: bold !important;
  color:#044983 !important;
  width:100% !important;
}
.sublogo {
  clear:both !important;
  text-align:center !important;
  width:100% !important;
  padding: 3px 0px 11px 0px !important;
}
.banner {
	margin-top: 10px !important;
}
.postarea {
  background:none !important;
}
.postblock {
  background: #DDDDDD !important;
  border: 1px solid #CCCCCC !important;
  color: black !important;
  font-weight: bold !important;
  padding: 2px 5px 2px 5px !important;
}
.footer {
  text-align:center !important;
  font-size:12px !important;
}
.unkfunc {
	background:inherit !important;
	color:#789922 !important;
}
.filesize {
	text-decoration:none !important;
}
.filetitle {
	background:inherit !important;
	color:#044983 !important;
	font-size:14pt !important;
  font-weight: bold !important;
}
.postername {
  background:inherit !important;
  font-size:14pt !important;
  color:#117743 !important;
  font-weight: bold !important;
}
.postertrip {
	background:inherit !important;
	color:#228854 !important;
}
.oldpost {
  background:inherit !important;
  color:#044983 !important;
  font-weight:800 !important;
}
.omittedposts {
  background:inherit !important;
  color:#4d4d4d !important;
}
.reply {
	background:#DDDDDD !important;
	color:#000000 !important;
	border:1px solid #CCCCCC !important;
	-moz-border-radius: 5px !important;
	-webkit-border-radius: 5px !important;
}
.replyhl {
	background:#D6BAD0 !important;
	color:#000000 !important;
}
.doubledash {
 	vertical-align:top !important;
	clear:both !important;
	float:left !important;
}
.replytitle {
	background:inherit !important;
	font-size:14pt !important;
	color:#044983 !important;
}
.commentpostername {
  background:inherit !important;
  font-size:14pt !important;
  color:#117743 !important;
  font-weight: bold !important;
}
a.quotejs {
  color:#000000 !important;
	text-decoration: none !important;
}
a.quotejs:hover {
  font-weight:bold !important;
}

.adminbar {
  text-align: center !important;
  background: none !important;
}

/* board list */

.category {
  color:#000000 !important;
  background:none !important;
  margin-bottom: 5px !important;
}

.header {
	background:#DDDDDD !important;
	color:#044983 !important;
	padding: 0px 5px 0px 5px !important;
	font-weight: bold !important;
	border: solid 1px #CCCCCC !important;
}

.list {
	padding: 5px !important;
}

/* admin posts */

.adminname {
	color: #af0a0f !important;
	
}

/* rules */

.rules table {
	margin: 5px 0px 5px 0px !important;
}

.prohibition {
	color: #af0a0f !important;
}

.prohibition ul, .info ul {
	margin: 0px !important;
	padding: 0px 0px 0px 20px !important;
}

/* title page */

.news {
	background:#DDDDDD !important;
	color:#044983 !important;
	padding: 0px 5px 0px 5px !important;
	font-weight: bold !important;
	margin-bottom: 5px !important;
	text-align: center !important;
}

.greeting {
	text-align: center !important;
	font-weight: bold !important;
	color: #044983 !important;
}

.spoiler {
	background-color: #CCCCCC !important;
	color: #CCCCCC !important;f
}

.spoiler:hover {
	background: #CCCCCC !important;
	color: #000000 !important;
}


.captcha {
        color: #af0a0f !important;
	/*
	  Foreground color of the letters on pictures generated by captcha.pl.
	  Don't use comments right before declaration of this class.
	*/
}
.thumbnailmsg {
	color:#000000 !important;
}
.highlight {
	background:#c3d6e5 !important;
	color:#044983 !important;
	border: 1px solid #044983 !important;
}
#visited {
  color:#0066ff !important;
  font-weight: bold !important;
}
table,tr,td {
  border: 0px  !important;
}
blockquote {
  margin-top: 10px !important;
  margin-bottom: 10px !important;
  margin-left: 20px !important;
  margin-right: 10px !important;
}
P {
  margin-bottom: 0px !important;
  margin-top: 10px !important;
}
.thumb {
  margin-bottom: 10px !important;
}
label {
  margin-left: 2px !important;
}
hr {
  border-style: none !important;
  background-color:#CCCCCC !important;
  height: 1px !important;
}
}
