css:
@-moz-document domain("roblox.com") {
@keyframes rainbow {
    0% {
        background-color: #ffafff;
        border-color: #ffafff;
    }
    13% {
        background-color: #ffb6c1;
        border-color: #ffb6c1;
    }
    25% {
        background-color: #e0bfb8;
        border-color: #e0bfb8;
    }
    38% {
        background-color: #ffb6c1;
        border-color: #ffb6c1;
    }
    50% {
        background-color: #ffafff;
        border-color: #ffafff;
    }
    63% {
        background-color: #ffb6c1;
        border-color: #ffb6c1;
    }
    75% {
        background-color: #e0bfb8;
        border-color: #e0bfb8;
    }
    88% {
        background-color: #ffb6c1;
        border-color: #ffb6c1;
    }
    100% {
        background-color: #ffafff;
        border-color: #ffafff;
}
}

.btn-common-play-game-lg
{
  animation: rainbow 2s infinite;
}
}
