css:
@-moz-document domain("73.popmundo.com"), domain("74.popmundo.com"), domain("75.popmundo.com") {
div#header
  {
    background-image: url(https://i.imgur.com/MO3KTla.png);
  }

  div#character-tools
  {
    background-image: url(https://i.imgur.com/SmEEDYb.png);
  }

  div#top-menu
  {
    background-image: url(https://i.imgur.com/DBnk37m.png);
  }

  div#notifications div.notification-loading
  {
    background-color: #ddd;
    color: #999;
  }

  div#ppm-main
  {
    background-image: url(https://i.imgur.com/faLGKUj.png);
  }

  div.box h2
  {
    font-size: 12px;
    background-image: url(https://i.imgur.com/V8JXJt6.png);
    background-color: #56686f;
    background-repeat: repeat-x;
    color: #fff;
    font-weight: 500;
  }

  body
  {
    background-color: #63767f;
    background-image: url(https://i.imgur.com/3z52uXn.png);
    background-repeat: repeat-x;
  }

  div#character-tools-shortcuts a.expandable, div#character-tools-account a.expandable
  {
    background-color: #a9b6ba;
  }

  a
  {
    color: #09639a;
    text-decoration: none;
  }

  div#top-menu-nav ul li a.active
  {
    background: url(https://i.imgur.com/EbEwNlI.png) repeat-x;
  }

  table.data th
  {
    background-image: url(https://i.imgur.com/V8JXJt6.png);
    background-color: #979795;
    background-repeat: repeat-x;
    color: #fff;
    font-weight: 500;
  }

  div#header-logo
  {
    background: url(https://i.imgur.com/gSoZTmO.png) no-repeat 37px !important;
    position: relative;
  /* z-index: 1000; */
    width: 365px;
    height: 97px;
    left: 180px;
    top: -25px;
  }

  div#header-logo  img
  {
    display: none;
  }

  table.hoverable tr:hover td,
table.hoverable tr.selected td,
table.data tr.hoverable:hover td,
table.data tr.selected td,
div#statusbar div.statusbarwrapper a.selected
  {
    background: #f7cf2a !important;
  }

  table.sortable th.headerAsc
  {
    background-image: url(https://i.imgur.com/ABLDf2R.png)!important;
    background-color: #4a5d63 !important;
  }

  table.data th
  {
    background-color: #4a5d63 !important;
  }

  div.entityLogoNoImg
  {
    background-color: #67777f;
    background-repeat: repeat-x;
    color: #fff;
  }

div.box table.data th {
    background-image: url(https://i.imgur.com/V8JXJt6.png);
    background-color: #56686f;
    background-repeat: repeat-x;
    color: #fff;
}


}
