css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cs-manager.com") {

body {	
background: #000000 !important;
color: #EEEEEE !important;
}

a {
color: #DDDDDD !important;
}

td {
color: #DDDDDD !important;
border: 1px solid #555555 !important;
}

textarea,input,select {
color: #DDDDDD !important;
}
#nav,#tab_container {
background-color: #000000 !important;
}

#header {
	background-color: #000000 !important;
	background-image: url("http://www.sp-in.dk/csm/theme/black/logo_black.png") !important;
	background-repeat: no-repeat !important;
	border-right: 1px solid #A2A2A2 !important;
	border-bottom: 1px solid #A2A2A2 !important;
	border-left: none;
	border-top: none;
}

#header img {	visibility: hidden !important;} 
#header a img {	visibility: hidden !important;} 


#container, #footer_container,.post,#content {
	color: #DDDDDD !important;
	background: #000000 !important;
	border-right: 1px solid #A2A2A2 !important;
	border-bottom: none !important;
	border-left: none !important;
	border-top: none !important;
}

table.rank,table.login {
	border: 1px solid #A2A2A2 !important; 
}
 
table.rank,table.login,table.mail,table.info {
	background: #000000 !important;
}
td.space,div.space {
	background: #000000 !important;
	border:none !important;
}
a.player,td.player,td.top,td.tactic,a.tactic,a.lineup,a.challenge,div.challenge {
	background: #666666 !important;
	color: #EEEEEE !important;
}
a.tactic:hover,a.lineup:hover,a.challenge:hover {
	background: #444444 !important;
}
div.disabled {
 background-color: #999999 !important;
}
td.position {
	background: #222222 !important;
}

td.head, h2, h3 {
	color: #EFEFEF !important;
	background-color: #000000 !important;
	background-image: none !important;
	border: 3px groove #999999 !important;
	border-top: 0px;
}

li a {
	background: #222222 !important;
	color: #DDDDDD !important;
	font-style: normal !important;
	border-bottom: 1px solid #A2A2A2 !important;
}

ul#menu li a {
	border-bottom: 1px solid #A2A2A2 !important;
	border-right: none !important;
	border-left: none !important;
	border-top: none !important;
	background: #000000 !important; 
	color: #DDDDDD !important;
}

ul#tab li a {
	border-width: 1px !important;
        border-style: solid !important;
        border-color: #A2A2A2 !important;
}

ul#tabtop li a {
	border-width: 1px !important;
        border-style: solid !important;
        border-color: #A2A2A2 !important;
}

li a:hover {
	background: #888888 !important;
	font-style: normal !important;
}

ul#tab li span {
	background: #000000 !important;
	color: #DDDDDD !important;
	font-style: normal !important;
	border-bottom: #000000 !important;
}

li span {
	color: #DDDDDD !important;
}

table.mail tr.head td, table.mail td.select, table.mail td.delete {
	background-color: #444444 !important;
}

#menu, #footer_left, #footer_right, div#container>div#info {
	background: #000000 !important;
}

form#search {
	border-bottom: 1px solid #A2A2A2 !important;
	background: #222222 !important;
}

input,select,textarea,radio {
	background: #000000 !important;
	border: 1px solid #A2A2A2 !important;
}
.buttonsmall, .button {
	background: #000000 !important;
	color: #FFFFFF !important;
	border-top: 2px solid #777777 !important;
	border-left: 2px solid #777777 !important;
	border-right: 2px solid #EEEEEE !important;
	border-bottom: 2px solid #EEEEEE !important;
	font-size: 10px !important;
	padding: 0px !important;
}

td.value {
	background: #666666 !important;
}
td.foot,td.info,td.week {
	background: #222222 !important;
}

#training_map,#training_player,#skill,#trainer,#training_players {
	background: #222222 !important;
}
div#training_type {
	background: #444444 !important;
}

tr.even {
	background: #666666 !important;
}
tr.odd {
	background: #000000 !important;
}
td.quote {
background-color: #555555 !important;
border: 1px dashed #CCCCCC !important;
}
td.quote a {
text-decoration: underline !important;
}
div.modify {
	background: #000000 !important;
}
}
