css:
@-moz-document domain("www.nitrotype.com")
{
body {
    background: none;
}
    
html {
    background: fixed url("/*[[bgi]]*/"), #616161;
    
/* background: fixed url("https://imgur.com/qzOcCuI.jpg"), #000;
   background: fixed url("https://www.teahub.io/photos/full/293-2938234_nascar-background.jpg"), #000; */
    
    background-size: 100%;
}

.bg--gradient {
    background: fixed;
}
.theme--pDefault .profile-bg
  {
    background: url("https://i.pinimg.com/originals/d0/9c/4c/d09c4cfd1b93cc66dc13c8b461fd2145.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #ffffff 100%);
    color: #d3d3d3;
    background-size: 100%
  }

  .theme--pDefault .profile-bgHead
  {
    background: url("https://i.pinimg.com/originals/d0/9c/4c/d09c4cfd1b93cc66dc13c8b461fd2145.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #ffffff 100%);
   background-size: 100%
  }

  .theme--pDefault.is-gold .profile-bg
  {
    background: url("https://i.pinimg.com/originals/d0/9c/4c/d09c4cfd1b93cc66dc13c8b461fd2145.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #ffffff 100%);
   background-size: 100%
  }

.card--grit {
    background: none;
}
.card--shadow-l {
    background: black
}

  .card--a
  {
    background-color: #fff0;
    color: #ffffff;
  }

  .card--shadow-l
  {
    box-shadow: none;
  }
  .theme--pDefault .profile-levelNum {
    color: #ffffff;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
}
.news--solo .news-body p {
    color: #ffffff;
    line-height: 1.8;
}
.news-teaser {
    color: #ffffff;
    margin-bottom: 40px;
}
.card--a {
    color: #ffffff;
    background: transparent;
}
.card--shadow-l {
    box-shadow: none;
}
.btn--inky {
    color: #fff;
}
.card--b {
    color: #fff;
}
.theme--pDefault .profile-body {
    color: #ffffff;
}

.dash-copyContainer {
    background: #1d1e23;
    box-shadow: 0 1px 157px 10px rgba(0, 0, 0, 0.78);
}
.dash-copy {
    color: /*[[hex]]*/;
    font-size: 28px;
}
.btn--primary {
    color: #FFF;
    background-size: 100%;
    background-color: #000 !important;
   
}
.btn--secondary {
    color: #FFF;
    background-size: 100%;
    background-color: #000 !important;


}
.growl--a {
    background: url("https://cdn.wallpapersafari.com/54/44/mLDa0d.jpg") center;
    color: #FFF;
    background-size: 100%;
    background-color: #000 !important;
}
}
