css:
@-moz-document regexp("https://www.google.com/(webhp|search|preferences).*"), regexp("https://www.google.com/\\?.*") {
  html {
      filter: invert(1);
  }

  img, svg, span[style^="background-position:"], div[role="navigation"] table tr td {
      filter: invert(1);
  }

  a {
      color: darkgoldenrod;
  }
}
