css:
/* AGENT_SHEET */
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* GENERAL */
.autocomplete-richlistbox {
-moz-appearance: none !important;
opacity: 0.95 !important;
margin: 14px 10px 16px !important;
background: none !important;
color: #fff !important;
}
.autocomplete-richlistitem {
color: #fff !important;
}
panel[type="autocomplete-richlistbox"] {
-moz-appearance: none !important;
border-image-source: url("data:image/png;base64,") !important;
border-image-slice: 15 fill !important;
border-image-width: 15px !important;
border-image-repeat: repeat !important;
-moz-window-shadow: none !important;
background: none !important;
margin: -5px 0px 0px 0px !important;
}
panel[type="autocomplete-richlistbox"]
.autocomplete-richlistbox richlistitem:not([selected="true"]):nth-child(odd),
.autocomplete-richlistbox richlistitem:not([selected="true"]):nth-child(even) {
background-color: transparent !important;
}
/* TITLE TEXT */
.ac-comment {
color: #fff !important;
}
/* URL TEXT */
.ac-url-text:not([selected="true"]),
.ac-action-text:not([selected="true"]) {
color: #999 !important;
}
/* SWITCH TO TAB */
.autocomplete-richlistbox richlistitem[actiontype="switchtab"] > .ac-url-box > .ac-action-icon {
opacity: 0.5 !important;
list-style-image: url("data:image/png;base64,")
!important;
}
/* SWITCH TO TAB */
richlistitem[actiontype="switchtab"][selected="true"] > .ac-url-box > .ac-action-icon {
opacity: 1.0 !important;
}
/* SEPARATOR */
.autocomplete-richlistitem {
border-top: solid 0px !important;
border-bottom: solid 1px #555 !important;
color: #fff !important;
}
/* SELECTED ITEM */
.autocomplete-richlistitem[selected="true"] {
background: -moz-linear-gradient(top, #6f81f5, #3f51f2) repeat-x !important;
border-top: solid 0px !important;
color: #fff !important;
}
.ac-url-text[selected="true"] {
color: #fff !important;
}
/* SELECTED ITEM (GRAPHITE) */
.autocomplete-richlistitem[selected="true"]:-moz-system-metric(mac-graphite-theme) {
background: -moz-linear-gradient(top, #7f868d, #69717c) repeat-x !important;
}
/* HIDE SCROLLBARS */
.autocomplete-richlistbox scrollbar[orient="vertical"] {
opacity: 0.0 !important;
margin-right: -15px !important;
}
/* BOOKMARKED RESULTS ICON */
richlistitem .ac-result-type-bookmark {
list-style-image: url("data:image/png;base64,")
!important;
-moz-image-region: rect(0px 16px 16px 0px) !important;
opacity: 0.4 !important;
}
/* BOOKMARKED RESULTS ICON (SELECTED) */
richlistitem[selected="true"] .ac-result-type-bookmark {
opacity: 1.0 !important;
}
/* TAGGED RESULTS ICON */
richlistitem .ac-result-type-tag {
list-style-image: url("data:image/png;base64,")
!important;
opacity: 0.4 !important;
}
/* TAGGED RESULTS ICON (SELECTED) */
richlistitem[selected="true"] .ac-result-type-tag {
opacity: 1.0 !important;
}
