/* Like/Dislike Animation */
@keyframes Like {
	0% {
		transform: rotate(5deg) scale(0.5);
		opacity: 0;
	}
  45% {
		transform: rotate(-7.5deg) scale(1.1);
	}
	65% {
		transform: rotate(5deg) scale(1);
		opacity: 1;
	}
	100% {
		transform: rotate(0deg);
	}
}

@keyframes Dislike {
	0% {
		transform: rotate(5deg) scale(0.75);
		opacity: 0;
	}
  50% {
		transform: rotate(-7.5deg) scale(1);
		opacity: 1;
	}
	100% {
		transform: rotate(0deg);
	}
}

