css:
@-moz-document domain("wikipedia.org") {
html {
    filter: invert(1);
}

body, body > * {
    background: #EEE !important;
}

a {
    color: darkgoldenrod !important;
}

a:visited {
    color: saddlebrown;
}
 
img, svg {
    filter: invert(1);
    background: #EEE;
    opacity: .9;
}

.mw-body {
	border: 1px solid #CCC;
}

.vector-menu-tabs, .vector-menu-tabs a, .vector-menu-tabs li, #mw-head .vector-menu-dropdown h3 {
    background-image: linear-gradient(to bottom,rgba(220,220,220,0) 0,#CCC 100%)
}

.vector-menu-tabs .selected {
    background: #EEE;
}

img[class^="mwe-math"] {
    filter: invert(0);
}
}
