@namespace url(http://www.w3.org/1999/xhtml);
/**
 *
 * Coded by Amol S Deshmukh /a mol web AT yah oo DOT com/
 *
 */
@-moz-document domain(google.com) 
{ /* BEGIN */



/* ------------------------------------------ */
/*   links to other google web sites          */
/* ------------------------------------------ */


a.q {
  font-size: 11pt;
  margin: 20px;
  padding: 5px;
  padding-bottom: 1px;
  border: solid 1px white;
  text-decoration: none;
}
a.q:hover {
  text-decoration: none;
  font-size: 11pt;
  margin: 20px;
  padding: 5px;
  padding-bottom: 1px;
  background-color: #E5ECF9;
  border-top: none;
  border-left: solid 1px #E5ECF9;
  border-right: solid 1px #E5ECF9;
  border-bottom: solid 2px #36c;
  -moz-border-radius-topleft:7px;
  -moz-border-radius-topright:7px;
  cursor: default;
}




/* ------------------------------------------ */
/*               special buttons              */
/* ------------------------------------------ */


[name="btnI"] {
  color: #444;
  background-color: #f5f5f5;
  border-top: solid 2px #444;
  border-left: solid 1px #eee;
  border-bottom: solid 1px #eee;
  border-right: solid 1px #eee;
  -moz-border-radius-bottomleft:7px;
  -moz-border-radius-bottomright:7px;
  margin: 10px;
}




/* ------------------------------------------ */
/*     query form-fields on *.google.com      */
/* ------------------------------------------ */


[name="q"] {
  width:720px; 
  font-size:10pt; 
  font-weight: normal;
  font-family: "verdana";
  color: #000;
  background-color: #E5ECF9;  
  padding: 4px 3px;
  border: solid 2px white;
  border-bottom: solid 2px #3366CC;
  -moz-border-radius-topleft:7px;
  -moz-border-radius-topright:7px;
}
[name="saddr"] {
  width:400px; 
  font-size:10pt; 
  font-weight: normal;
  font-family: "verdana";
  color: #000;
  background-color: #E5ECF9;  
  padding: 4px 3px;
  border: none;
  border-bottom: solid 2px #3366CC;
  -moz-border-radius-topleft:7px;
  -moz-border-radius-topright:7px;
}
[name="daddr"] {
  width:400px; 
  font-size:10pt; 
  font-weight: normal;
  font-family: "verdana";
  color: #000;
  background-color: #E5ECF9;  
  padding: 4px 3px;
  border: none;
  border-bottom: solid 2px #3366CC;
  -moz-border-radius-topleft:7px;
  -moz-border-radius-topright:7px;
}





/* ------------------------------------------ */
/*  Power Users don't need no clunky buttons  */
/* ------------------------------------------ */


[name="btnG"] {
  display: none;
}
[name="btnmeta=search=search"] {
  display: none;
}
[name="qt_s"] {
  display: none;
}


} /* END */
