css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("sueddeutsche.de") {
	
	#artikel, .nMain, .content, #contentTable, .artikelBox, .hidePrint, .breadcrumb, #wrapper {
		width: auto !important;
	}
	
	.content > table {
		width: auto !important;
	}
	
	div#contentcolumn {
		width: 95% !important;
		text-align:justify;
	}

	div#sueddeutsche{
		position:absolute !important;
		top:0 !important;
		width:100% !important;
	}
	.sueddeutsche {
		width: 100% !important;
	}

	
	#nnav-bgheader, #nnav-oly, #redaktion, .stoerBS, #artikelfoot, #foo-oly, .foo-oly, .photo.right, .similar-article-box, .rechteSpalte, .kommentare, .footer, .photo.wide, #themenbox, .bgcontent.absatz, .articleDistractor, .right, #sdesiteheader, ul#sidebarcolumn, div.articlefooter, div.bildbanderolle, div.bildaufmacher, ul#articlesidebar, div#relatedcontent, div#bannerSuperBanner, div#N_AD, div#CAD_AD, div.headslot, div.themenbox , div.basebox {
		display: none !important;
		position: absolute;
		top: -500px;
	}
	
	.artikelheader.clr, #contentTable{
		background: none !important;
		border-bottom: 1px solid #B2B2B2 !important;
	}
}
