css:
@-moz-document domain("startpage.com") {
/*   -=< Darker Startpage by Zigboom Designs >=-   */

html, html, .layout-web__body, .w-gl, .layout-web, #main, .layout-video, .footer__container, .layout-news {
    background: #181818 !important;
}

header, .home-wrapper, .header, .widget-install-sp .widget-install-sp-container, .w-gl__result, .pagination, .footer__container, .w-gl__label, .search-filter-family__toggle, .wp-qi-sb__result, .home, .feedback-button, .sp-gl--blak .sp-gl__container, .sp-gl--nite .sp-gl__container, .hamburger-menu__drawer__nav-link--startmail:before, .search-filters-toolbar .settings:hover, .settings, .widget-install-privacy-protect .widget-install-privacy-protect-container, #slave-1-1 {
    filter: grayscale(1);
}

header, .header {
    background: #181818 !important;
    box-shadow: 0 1px 1px 0px #000, 0 0px 7px #000 !important;
}

.hamburger-menu__drawer {
    background: #181818 !important;
    border: none !important;
    box-shadow: none !important;
    height: 100vh !important;
}

.hamburger-menu .dark-overlay {
    background: rgba(0,0,0,.4);
}

.hamburger-menu__drawer__section:not(:last-child) {
    border-bottom: 1px solid #333 !important;
}

.cn-qi-tp__result, .cn-qi-tp__results-container {
    border-color: #333 !important;
}

.cn-qi-tp__result:hover, .cn-qi-tp__see-more:hover {
    background: #282828 !important;
}

.w-gl__result a, .vo-yt__title, .vo-yt__metadata, .vo-yt__publish-info, .nc-nr-em__title, .cn-bg .second-line-container .date, .cn-bg .second-line-container .source, .cn-bg .article .title, .cn-bg .second-line-container .anonymous-view-url, .hamburger-menu__drawer a, .si27 {
    color: #dcdcdc !important;
}

a:visited, a:visited, .w-gl__description, .cn-qi-tp__meta__publish-date {
    color: #aaa !important;
}

.w-gl__result-title:hover, .w-gl__result a:hover, .hamburger-menu__drawer a:hover, .si27:hover {
    text-decoration: none !important;
    color: #fff !important;
}
}
