css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.honda-tech.com"), domain("honda-tech.com") {
 strong a{
  text-decoration:none!important;
  color:#333333;
 }
 center{
  display:none!important;
 }
 strong a:hover, strong a:active{
  color:#000000!important;
  border-bottom:1px dotted #0099FF;
 }
 a.bigusername{
  color:#333333!important;
  text-decoration:none!important;
  font-size:14px!important;
 }
 a.bigusername:hover, a.bigusername:hover{
  color:#000000!important;
  text-decoration:none!important;
  border-bottom:1px dotted #0099FF!important;
  font-weight:bold;
 }
 a[onclick="return log_out('Are you sure you want to log out?')"]{
  color:#333333!important;
  text-decoration:none!important;
 }
 a[onclick="return log_out('Are you sure you want to log out?')"]:hover,a[onclick="return log_out('Are you sure you want to log out?')"]:active{
  color:#000000!important;
  border-bottom:1px dotted #0099FF!important;
  font-weight:bold;
 }
 .page{
  background-color:#CCCCCC!important;
  -moz-border-radius:5px!important;
 }
 .tborder{
  -moz-border-radius:5px!important;
 }
 .tborder td.alt1, .tborder td.alt2{
  background:none!important;
 }
 td.alt1Active div.smallfont, td.alt1 div.smallfont{
  display:none!important;
 }
 div.smallfont div[style="white-space: nowrap;"]{
  display:none;
 }
 div.smallfont br{
  display:none;
 }
 div.smallfont a[rel="nofollow"]{
  color:#333333;
  text-decoration:none!important;
 }
 div.smallfont a[rel="nofollow"]:hover{
  color:#000000!important;
  border-bottom:1px dotted #0099FF;
  font-weight:bold!important;
 }
 span.time{
  color:#0099FF!important;
 }
 td.alt2 a{
  color:#333333!important;
  text-decoration:none!important;
 }
 td.alt2 a:hover, alt2 a:active{
  color:#000000!important;
  border-bottom:1px dotted #0099FF!important;
  font-weight:bold!important;
 }
 td.alt2[style="border: 1px inset ;"] img{
  width:50px!important;
  height:50px!important;
 }
 td.alt2[style="border: 1px inset ;"] img[src^="http://images.ibsrv.net/ibsrv/res/src:honda-tech.com"]{
  width:inherit!important;
  height:inherit!important;
 }
 td.alt2[style="border: 1px inset ;"] img:hover{
  width:inherit!important;
  height:inherit!important;
 }
 .tcat{
  background-color:#0099FF!important;
  -moz-border-radius-topleft:5px!important;
 }
 .vbmenu_control{
  background-color:#333333!important;
 }
 .vbmenu_control a{
  text-decoration:none!important;
  color:#FFFFFF!important;
 }
 .vbmenu_control a:hover{
  text-decoration:none!important;
  color:#FFFFFF!important;
  border:none!important;
 }
 .vbmenu_control#forumsearch{
  -moz-border-radius-topright:5px!important;
 }
 .vbmenu_popup{
  border:1px solid #333333!important;
  -moz-border-radius-bottomleft:5px!important;
  -moz-border-radius-bottomright:5px!important;
 }
 .vbmenu_hilite{
  background-color:#333333!important;
  -moz-border-radius-bottomleft:5px!important;
  -moz-border-radius-bottomright:5px!important;
 }
 td.alt1Active a, td.alt1 a{
  text-decoration:none!important;
  color:#333333!important;
  text-transform:lowercase!important;
 }
 td.alt1Active a:hover, td.alt1 a:hover{
  color:#000000!important;
  border-bottom:1px dotted #0099FF!important;
  font-weight:bold!important;
  text-transform:uppercase!important;
 }
 table{
  border:none!important;
  border-spacing:0px!important;
 }
 .thead{
  -moz-border-radius-bottomleft:5px!important;
  -moz-border-radius-bottomright:5px!important;
 }
 .thead, .thead a{
  background-color:#1b1b1b!important;
  color:#0099FF!important;
  text-decoration:none!important;
  font-weight:normal!important;
 }
 .thead a:hover{
  color:#FFFFFF!important;
  border-bottom:1px dotted #0099FF!important;
 }
 .tfoot{
  -moz-border-radius-bottomleft:5px!important;
  -moz-border-radius-bottomright:5px!important;
 }
 .tfoot, .tfoot a, a[accesskey="9"]{
  background-color:#1b1b1b!important;
  color:#0099FF!important;
  text-decoration:none!important;
  font-weight:normal!important;
 }
 .tfoot a:hover{
  color:#FFFFFF!important;
  border-bottom:1px dotted #0099FF!important;
 }
}
