@-moz-document domain("alfalfa.livedoor.biz") {
/* template v4 beta update 2008/04/14 r2 */
* {
background-image : none !important;
float : none !important;
text-align : left !important;
letter-spacing : 1px !important;
line-height : 1.5 !important;
min-width : 0 !important;
text-indent : 0 !important;
white-space : normal !important;
list-style-image : none !important;
}
html {
font-size : 100% !important;
background-color : #dcdcd4 !important;
}
body {
font-size : 112.5% !important;
margin : 0 auto !important;
padding : 0 !important;
background-color : #dcdcd4 !important;
color : #000 !important;
width : 40em !important;
max-width : 100% !important;
border : 0 !important;
}
table[width]
, tbody[width]
, thead[width]
, tr[width]
, td[width]
{ width : auto !important; }
noscript
, iframe
, hr
, br[clear="all"]
, #footer
, #banner
, .footer
, .banner
, img[width="1"]
, img[width="0"]
, img[src^="http://ad"]
, *[id^="ad"]
, *[class^="ad"]
, *[id*="calendar"]
, *[class*="calendar"]
, *[id*="track"]
, *[class*="track"]
{ display : none !important; }
u { text-decoration : none !important; }
a { text-decoration : underline !important; }
font[size] { font-size : 100% !important; }
table[width] { max-width : 40em !important; }
/* /template */

/* destroy div layout */
#container
, #container div {
font-size : 100% !important;
position : static !important;
background-color : transparent !important;
width : auto !important;
height : auto !important;
border : 0 !important;
margin : 0 !important;
padding : 0 !important;
}
#container * {
font-size : 100% !important;
position : static !important;
background-color : transparent !important;
width : auto !important;
height : auto !important;
color : #000 !important;
}
#container a:link { color : #0000ee !important; }
#container a:visited { color : #551a8b !important; }
#container a:hover { color : #ee0000 !important; }
#container a:hover { background-color : transparent !important; }
/* /destroy div layout */

#axL1
, #banner .description
, .posted4
, .menu3
, .titlebody > a
, .posted3
, #right
, #left
, .menu
, .an
, .menu4 img
, #comment > h3
, .formbodybottom
, .fullbody
{ display : none !important; }

.blogtitle
{ padding : 0 !important; }

#container #blog
{ margin : 1em 0 !important; }

#container .main
, #container #comment
, h1
{ margin-top : 1em !important; }

#container .commenttext
{ margin-bottom : 1em !important; }

#container #banner
, #container .trackbackurltop
{ display : block !important; }

.blogbody li
{ list-style-type : none !important; }

#container .commenttext
{ font-weight : normal !important; }

#container .commentttl a
{ text-decoration : none !important; }

body
{ font-size : 100% !important; }

}
