css:
@-moz-document url-prefix("https://www.hyatt.com/search"), regexp("https://www.hyatt.com/.*/search/.*") {
    .p-hotel-card-hbj[data-spirit-code]::after {
      content: attr(data-spirit-code);
      position: relative;
      text-align: right;
      font-family: monospace;
      width: 0;
      right: 45px;
      top: 135px;
    }
}


@-moz-document url-prefix("https://www.hyatt.com/explore-hotels"), regexp("https://www.hyatt.com/.*/explore-hotels/.*") {
  .PEAK {
      background-color: #FFE5E9;
  }

  .PEAK .points-level:after {
      content: "PEAK";
      border: solid 1.5px #F08080;
      border-radius: 10.5px;
      padding: 3px 10px;
      font-size: 10px;
      font-weight: 700;
  }

  [data-js-property]::after {
    content: "["attr(data-js-property)"]";
    font-family: monospace;
    padding-left: 5px;
  }
  
  @media screen and (min-width: 960px) {
    [data-spiritcode]::after {
      content: attr(data-spiritcode);
      position: relative;
      text-align: right;
      font-family: monospace;
      
      padding-right: 10px;
      height: 0;
      bottom: 90px;
    }
  }
  
  @media screen and (max-width: 959px) and (min-width: 768px) {
    [data-spiritcode]::after {
      content: attr(data-spiritcode);
      position: relative;
      text-align: right;
      font-family: monospace;
      
      width: 0;
      right: 45px;
      top: 310px;
    }
  }
  
  @media screen and (max-width: 767px) {
    [data-spiritcode]::after {
      content: attr(data-spiritcode);
      position: relative;
      text-align: right;
      font-family: monospace;
      
      width: 0;
      right: 45px;
      top: 180px;
    }
  }
}
