css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ogame.ru")
{
	/* Enlarge the tooltip showing the fleet contents (ships and resources) 
	   in the expanded event list. Russian universes only: */
	div.clue-right-event
	{
		width: 200px !important;
	}
	/* Get back in place the runaway "Back" and "Next" buttons on the fleet dispatch pages: */
	div#steps
	{
		margin: 0 0 0 0 !important;
	}
	ul#fleetBriefingPart1_2
	{
		margin-left: 15px !important;
	}
}

@-moz-document domain("ogame.org"),
domain("ogame.com.br"),
domain("ogame.cz"),
domain("ogame.de"),
domain("ogame.dk"),
domain("ogame.com.es"),
domain("ogame.fr"),
domain("ogame.gr"),
domain("ogame.com.hr"),
domain("ogame.hu"),
domain("ogame.it"),
domain("ogame.jp"),
domain("ogame.nl"),
domain("ogame.no"),
domain("ogame.pl"),
domain("ogame.com.pt"),
domain("ogame.ro"),
domain("ogame.ru"),
domain("ogame.se"),
domain("ogame.si"),
domain("ogame.sk"),
domain("ogame.com.tr"),
domain("ogame.tw"),
domain("ogame.us")
{
	/* Color lime-green the sentence that shows the energy production of a
	   solar sat and the range of the IPMs and the sensor phalanx: */
	.solarSatEnergyInfo,
	#solarSatEnergyInfo
	{
		color: lime !important;
	}
	/* Right-align the numbers in the espionage reports: */
	#netz .contentz table td.value,
	#netz .contentz table.spy2 td,
	td.round_attacker table td,
	td.round_defender table td,
	td.value,
	#messagebox table.spy2 td
	{
		text-align: right !important;
	}
	/* Make sure each entry in the spy report fits in a single line: */
	#netz .contentz table td.key
	{
		width: 200px !important;
	}
	/* Center properly the "Production factor" text on the resource 
	   settings page and align the Recalculate button so that Antigame
	   doesn't put its "0%" and "100%" buttons on a new line: */
	#resourceSettings td#factor span.factorkey,
	#resourceSettings td#factor span.factorbutton
	{
		float:none !important;
		margin-left: 0px !important;
		display: block !important;
	}
	span.factorbutton input.button188
	{
		display: inline !important;
		margin: 0 auto !important;
	}
	/* Enlarge the input boxes for coordinates in Galaxy
	   view, so that the numbers aren't too low: */
	#galaxyscroll input,
	#solarscroll input
	{
		height: 24px !important;
		padding: 0 0 0 !important;
	}
	/* Make sure the word "Clock" isn't truncated in the flight 
	   times on left part of the fleet movement page: */
	div.fleetDetails span.nextabsTime,
	div.detailsOpened span.nextTimer,
	div.detailsOpened span.nextMission
	{
		width: 110px !important;
		left: 545px !important;
	}
	/* Prevent the wrapping of the date and the From 
	   field of the messages in the message box: */
	table.list td.date,
	table.list th.date
	{
		width: 150px !important;
	}
	table.list td.from,
	table.list th.from
	{
		width: 155px !important;
	}
	/* Center the headers of the fleet movement tooltip: */
	#cluetip-inner table.fleetinfo th,
	#movement table.fleetinfo th
	{
		text-align: center !important;
	}
	/* Color the "Relocate" button in Overview as a premium feature and in bold: */
	span.planetMoveOverviewMoveLink
	{
		color: #FFD700 !important;
		font-weight: bold !important;
	}
	div.content-box-s,
	#pqueue
	{
		margin-top: 0px !important;
	}
	/* Put the stupid footer at the bottom of the page instead of the bottom of the screen: */
	#siteFooter
	{
		position: relative !important;
		bottom: -32px !important;
	}
	/* Fix the footer width for long texts like in Russian: */
	#siteFooter .content
	{
		width: 870px !important;
	}
	#siteFooter .fright
	{
		width: 450px !important;
	}
	/* Lower the number of messages text to make it better visible: */
	#message_alert_box span
	{
		margin-top: 8px !important;
	}
	/* Put the icons for colonization and planet relocation
	   into the action icon slots to the right: */
	#galaxy .planetMoveIcons,
	#galaxy a.planetMoveInProgress
	{
		position: absolute !important;
		margin-top: -9px !important;
	}
	#galaxy .planetMoveOk,
	#galaxy .planetMoveDefault,
	#galaxy .planetMoveBreakup,
	#galaxy .planetMoveInactive,
	#galaxy .planetMoveGalaxyCooldown
	{
		left: 630px !important;
	}
	#galaxy .colonize-inactive,
	#galaxy .colonize-active
	{
		left: 575px !important;
	}
	/* Move the advertisement banner a bit to the right,
	   so that it doesn't cover the planet column: */
	div#banner_skyscraper
	{
		right: -140px !important;
	}
	/* Move the pseudo-windows frames a bit higher: */
	div#TB_window
	{
		margin-top: -320px !important;
	}
	/* Dim the icons of the returning fleets: */
	tr.eventFleet>td.icon_movement_reserve
	/* tr.reverse td */
	{
		opacity: 0.3 !important;
	}
	/* Expand the message window size, so that the combat reports can fit: */
	#messagebox .textWrapper
	{
		height: 370px !important;
	}
	/* Compress the messages in the message box: */
	#mailz td
	{
		padding-top: 2px !important;
		padding-bottom: 4px !important;
	}
	/* Compress the alliance members list: */
	#alliance .members td
	{
		padding-top: 0px !important;
		padding-bottom: 0px !important;
	}
	/* Move the fadeBox away from the input field on the Shipyard and Defense pages: */
	#shipyard .fadeBox,
	#defense .fadeBox
	{
		top: 50% !important;
	}
	#planetdata
	{
		height: 105px !important;
	}
	/* Hide from the combat reports the explanatory text about why the attacked fleet has not retreated: */
	#retreatText span,
	.retreatText span
	{
		display: none !important;
	}
	/* Compress the combat reports a bit: */
	#battlereport .winner
	{
		padding: 0 0 !important;
	}
	#battlereport .win,
	#battlereport .draw,
	#battlereport .lose,
	#battlereport div#combatants
	{
		border: none !important;
	}
	#shortreport td
	{
		padding: 0 5px !important;
	}
	/* Expand the ship selection input boxes a bit: */
	#fleet1 #buttonz div ul li input
	{
		left: 0 !important;
		width: 62px !important;
	}
	#fleet1 a.max
	{
		left: 65px !important;
	}
	#fleet3 #sendfleet
	{
		margin-top: 0 !important;
	}
	/* Non-hand cursor on uncolonized positions in Galaxy view: */
	#galaxy #galaxytable td.microplanet1
	{
		cursor: default !important;
	}
	/* Make sure the fleet action icons are over the debris numbers show by AntiGame: */
	#galaxy .fleetAction
	{
		z-index: 999 !important;
	}
	/* Show the activity number as black-on-orange instead of orange-on-black for better visibility: */
	#galaxy div.activity.showMinutes
	{
		background-color: orange !important;
		color: black !important;
		font-weight: bold !important;
	}
	/* Non-hand curosor on the alliance members list in the Exodus dialog: */
	#exodus-ally-content #exodus-universe-treeview li
	{
		cursor: default !important;
	}
	/* Always show the additional Military stats buttons: */
	#highscore #subnav_fleet,
	#highscore #subnav_ally,
	#highscore #subnav_research
	{
		display: block !important;
	}
	/* Move up the "Activate" button on the item description in the Shop: */
	#itemDetails #features a.build-it.activateItem,
	#itemDetails #features a.build-it_disabled.activateItem
	{
		margin-top: 15px !important;
	}
	.detail_screen div#features a.build-it,
	.detail_screen div#features a.build-it_disabled
	{
		margin-top: 1px !important;
	}
	/* Color the bottom strip of the images on the Auction to show better their rank: */
	#traderOverview .div_trader .left_box .ecke
	{
		left: 3px !important;
		width: 137px !important;
		text-align: center !important;
	}
	.r_common_140px .ecke
	{
		color: white !important;
		background-color: sienna !important;
	}
	.r_uncommon_140px .ecke
	{
		color: black !important;
		background-color: silver !important;
	}
	.r_rare_140px .ecke
	{
		color: black !important;
		background-color: gold !important;
	}
	/* Make the input fields on the 1st and 2nd fleet dispatch pages narrower again: */
	#fleet1 input.fleetValues
	{
		height: 14px !important;
	}
	#fleet2 input.galaxy,
	#fleet2 input.system,
	#fleet2 input.planet
	{
		height: 16px !important;
	}
	/* Make better visible the fleet slot numbers in the galaxy: */
	#galaxy #probeValue,
	#galaxy #recyclerValue,
	#galaxy #missileValue,
	#galaxy #slotUsed
	{
		color: white !important;
	}
	/* Fix the width of the event list columns: */
	.originFleet span, .destFleet span
	{
		width: 100% !important;
	}
	/* Open the login form on the login page: */
	#login
	{
		display: block !important;
		left: 685px !important;
		top: 60px !important;
	}
}
