@-moz-document domain("meet.google.com") {
@keyframes BorderRainbow {
    0% {
        border: 4px solid rgb(235, 64, 52)
    }
    9% {
        border: 4px solid rgb(235, 131, 52)
    }
    18% {
        border: 4px solid rgb(235, 201, 52)
    }
    27% {
        border: 4px solid rgb(168, 235, 52)
    }
    36% {
        border: 4px solid rgb(86, 235, 52)
    }
    45% {
        border: 4px solid rgb(52, 235, 168)
    }
    54% {
        border: 4px solid rgb(52, 235, 220)
    }
    63% {
        border: 4px solid rgb(52, 192, 235)
    }
    72% {
        border: 4px solid rgb(52, 55, 235)
    }
    81% {
        border: 4px solid rgb(143, 52, 235)
    }
    90% {
        border: 4px solid rgb(232, 52, 235)
    }
    97% {
        border: 4px solid rgb(235, 52, 147)
    }
    100% {
        border: 4px solid rgb(235, 64, 52)
    }
}

