css:
@-moz-document domain("5e.tools")
{


@font-face{font-family:"ScalySansRemake";
    src:url('https://rawcdn.githack.com/5e-Cleric/fonts-/f37c99944fed0e5e01f082e511f104b39b7b4566/ScalySans/Scaly-Sans-Caps.woff') format('woff')} 
@font-face {
    font-family: "MrEavesSmallCaps";
    src:local("Mr Eaves Small Caps"),
        url("https://raw.githubusercontent.com/jonathonf/solbera-dnd-fonts/master/Mr%20Eaves/Mr%20Eaves%20Small%20Caps.otf") format("opentype");
			}

:root {
	--scroll: url("https://i.imgur.com/XJY6fa1.jpg");
    --baverage: #F2ECE4;
    --h1color:#58180D;
    --tdcolor:#E0E5C1;
    --titlecolor:#ED1C24;
    --underlinecolor:#f0bf48;
    --statblocklines:#9c2b1b;
    --redscroll:url(https://i.imgur.com/YvYGqOX.jpg);
    --bluescroll:url(https://i.imgur.com/w2m4BgW.jpg);
    --seascroll:url(https://i.imgur.com/dKciX3y.jpg);
}


body
  {
    background-color:#2C3E50;
                        background-image:var(--seascroll);
  }
.bkmv {
	background-color:#2C3E50;
    background-image:var(--seascroll);
}

.page__header {
	background-image:var(--redscroll);										/*aaaaaaaaaaaaaaaaaaaa*/
}
.nav.nav-pills.page__nav-inner {
	background-image:var(--seascroll);
    border-radius: 0px 0px 10px 10px;
}

.dropdown.dropdown--navbar.page__nav-hidden-mobile.page__btn-nav-root.active a {
	background-image:var(--bluescroll);
    border:none;
}
.dropdown.dropdown--navbar.page__nav-hidden-mobile.page__btn-nav-root.active a:hover ,.dropdown.dropdown--navbar.page__nav-hidden-mobile.page__btn-nav-root.active a:focus{
	background-image:var(--bluescroll) !important;
    background-color:#00000000 !important;
    filter:brightness(200%) !important;
}

.bk__stats--narrow{
	min-width:90%;
    padding-right:100px;
}


.view-col--wrp-book-contents{
    margin-top:100px;
    font-size:120%;
    min-width:300px;
    background-image:var(--scroll)!important;
}

.flex-col{
	padding-top:3px;
    padding-bottom:3px;
}


.contents-item{
	line-height:20px;
}

.bk__head-section--active {
    padding-top:3px;
    padding-bottom:3px;
	background-color:#00000000; 
}

.bk__head-chapter--active {
    padding-top:12px;
    padding-bottom:12px;
	background-image:var(--scroll);
}
.cls-nav {
    margin-left:-30px;
	min-width:300px;
    background-image:var(--scroll);
}
.cls-nav .nav-body{
	min-width:250px;
    font-size:15px;
}
.cls-nav .nav-body .cls-nav__item:hover{
	background-color:#eee;
}

.entry-title-inner {
	font-family:MrEavesSmallCaps;
	font-weight:900;
    font-size:25px;
	color:#58180D;
    line-height:1;
}

.rd__b-inset {
    margin: 20px 15px;
	font-size:.318cm;
	line-height:1.2em;
	background-color:#e0e5c1;
	border-style:solid;
	border-width:1px;
	border-image:url("data:image/png;base64,") 12 stretch;
	border-image-outset:9px 0px;
	border-image-width:11px;
	padding:.13cm .16cm;
	filter:drop-shadow(1px 4px 6px #888)
}
.rd__b-inset .entry-title-inner {
    font-family:ScalySansRemake;
    font-size:18px;
    color:#000;
    margin-bottom:7px;
}
.rd__b-inset p,.rd__b-inset dl,.rd__b-inset ul,.rd__b-inset ol{
	line-height:1.2em
}
.rd__b-inset ul,.rd__b-inset ol{
	padding-left:1em
}
.rd__b-inset em{
	font-style:italic
}
.rd__b-inset strong{
	font-weight:800;
	letter-spacing:-0.02em
}
.rd__b-inset h5+*{
	margin-top:.1cm
}
.rd__b-inset+*{
	margin-top:.45cm
}
.rd__b-inset h5{font-size:.375cm}.rd__b-inset p{
	display:block;padding-bottom:0px
}
.rd__b-inset p+p{
	padding-top:.8em
}
.rd__b-inset:last-child{
	margin-bottom:20px;
}

.cls-tbl__stripe-odd:nth-child(odd){
    background-color:#e0e5c1;
}

.book-view{
padding-left:0px;
}
.px-y{
    padding-left:1%;
    padding-right:2%;
}
.rd__hr--section {
	border-color: #414141;
}
.spicy-sauce:not(.subclass-feature):not(.subclass-feature--sub) .rd__h--0, .spicy-sauce:not(.subclass-feature):not(.subclass-feature--sub) .rd__h--1, .spicy-sauce:not(.subclass-feature):not(.subclass-feature--sub) .rd__h--2 {
    color:#822000;
    border-bottom: 1px solid #822000;
}
.rd__title-link:hover {
	opacity:1;
    font-weight:900;
}
.rd__h-toggle.ml-2.clickable:hover {
	opacity:1;
    font-weight:900;
}
a {
	color:#23ff3a;
}
#pagecontent a {
color:#347f17;
}
.cls__list {
    color:#fff;
    font-weight:10;
}

.render-roller {
	color:#a40c0c;
}
.text{
    min-width:40%;
    padding-left:5%;
    padding-right:10%;
	background-image:var(--scroll);
}
.text-center {
	margin-top:10px;
}

.stats .stripe-odd-table>tbody>tr:nth-child(odd), .stats .stripe-even-table>tbody>tr:nth-child(even){
	background-color:#E0E5C1;
}

caption{
    font-family:ScalySansRemake;
	font-size:.423cm;
	font-weight:900;
	color:#000;
}

.view-col-wrapper:before {
   position:absolute;
    left:200px;
    top:0px;
}


.col-md-9{
    min-width:40%;
}
.col-md-9 hr {
	border-top: 2px solid #822000;
}
.stats {
    padding-left:10px;
    padding-right:10px;
    background-image:var(--scroll);
}



.cls-main__linked-titles {
	margin-left:100px;
}
#pagecontent {
	border-collapse: unset;
}

.py-3.px-5 {
	background-image:var(--scroll);
    border: 1px solid #58180D;
    border-bottom: 2px solid #58180D;
}
.stats--book-large .rd__h--0 .entry-title-inner  {
    font-size:45px !important;
	font-family:MrEavesSmallCaps;
	font-weight:900;
	color:#58180D;
    line-height:1;
}
::-webkit-scrollbar {
    width: 9px;
    height: 9px;
}
::-webkit-scrollbar-thumb {
    background:url(https://i.imgur.com/hkN3N4E.png);
    background-position:center ;
    background-size:cover;
    min-height:40%;
}

.out-roll {
    background-color: #c5c5c5;
    background-image:url(https://i.imgur.com/eFBfaWu.png);
    background-size:60%;
    background-position:center;
    background-repeat:no-repeat;
}


.rollbox .ipt-roll {
    background: #ddd;
}

.list.contents.flex-col {
	padding-top:10px;
}

.monsters {
color:white;
}
.rollbox-min {
	color:#ffffff;
    background-color:#6c6c6c;
    width: 34px;
    height: 34px;
}
.glyphicon-chevron-up {
    padding-top:5px;
    padding-left:5px;
}

.list,.w-100:not(table) {
	color:#fff;
}

.w-100:not(table) a {
color:#23ff3a;
}
.split-v-center:hover {
	color:#fff;
    background-color:#17212b;
}
.split-v-center:hover .mobile__hidden {
	color:#d700ef;
}
footer {
	color:#fff;
}


a.dropdown-toggle:hover {
	color:white;
}
.btn-default {    /* buttons on the home page*/
	border: 2px solid;
    border-radius:10px;  
    transition-property:filter;
    transition-duration:0.3s;
}
.btn-default:hover {
	filter:invert(100%);
}


.bks__wrp-bookshelf-item {
	background-color:white;
    border-radius:10px;
  	transition-duration: 5s;
}

            .bks__wrp-bookshelf-item:hover {
				background-image:var(--scroll);
   			 	border-radius:50px;
			}
		img.mb-2.bks__bookshelf-image {
  			clip-path: inset(0% 12%  round 7% 7% 7% 7%);
		}
    	.bks__wrp-bookshelf-item .text-center {
  			transition-duration: 5s;
    	}


.page__nav-inner>li.active>a {
  	transition-duration: 0.3s;
}
.page__nav-inner>li.active>a:hover {
	filter:grayscale(100%);
}
.list.contents .ve-flex-col,.list.contents.ve-flex-col,.bk-contents .w-100 {
	color:#58180D !important;
}
.list.contents .ve-flex-col:hover,.list.contents.ve-flex-col:hover,.bk-contents .w-100:hover {
	color:#000 !important;
}



}
