css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.reddit.com/"){

.author:hover{
background:#369 !important;
color:#fff !important;
text-decoration:none !important;
}
.friend:hover{
background:orangered !important;
color:#fff !important;
}
.submitter:hover{
background:#0055DF !important;
color:#fff !important;
}
.moderator:hover{
background:#228822 !important;
color:#fff !important;
}
.admin:hover{
background:#FF0011 !important;
color:#fff !important;
}

.commentarea * .arrow{
position:relative !important;
left:-10px !important;
padding:3px !important;
}
.commentarea * .midcol{
overflow:visible !important;
}
.commentarea * #siteTable * .score{
position:relative !important;
left:-10px !important;
}
.commentarea * .usertext-body{
padding-left:6px !important;
}

.commentarea * .arrow{
border:#336699 solid 1px !important;
}
.child * .arrow{
border-color:#B89F68 !important;
}
.child * .child * .arrow{
border-color:#81BD93 !important;
}
.child * .child * .child * .arrow{
border-color:#BD9981 !important;
}
.child * .child * .child * .child * .arrow{
border-color:#81BDB2 !important;
}
.child * .child * .child * .child * .child * .arrow{
border-color:#D0A9b2 !important;
}
.child * .child * .child * .child * .child * .child * .arrow{
border-color:#A9B2D0 !important;
}
.child * .child * .child * .child * .child * .child * .child * .arrow{
border-color:#B3B868 !important;
}
.child * .child * .child * .child * .child * .child * .child * .child * .arrow{
border-color:#DD9966 !important;
}
.child *.child * .child * .child * .child * .child * .child * .child * .child * .arrow{
border-color:#99AB85 !important;
}
}
