css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("notespark.com") {

/** {outline:none;}*/

.note_controls_right, #footer, #main_logo {display:none;}

body {
    background-image: none !important;
    background-color: #ccc !important;
}

#top_bar {
    height:55px !important;
background:url(data:image/png;base64,); 
}

#mainbox {
border:1px solid #969696;
background-color: #e8e8e8 !important;
border-top:none;
padding:0 !important;
}

#loginbar { color: #b8b8b8; }
#loginbar a {display:none !important; }
#loginbar form {
    margin-top:-20px !important;
    padding-top:4px !important;
}
#loginbar form input { height:20px !important; }

.left_pane_header, .right_pane_header {
background:url(data:image/png;base64,R0lGODlhAgAZAMQAAObn5uTm5eXm5eTk5OPk4+Li4uHh4eDg397f3t3e3dvd29vb2tnZ2dfY2NbX19XW1dTU09LT09HS0dDR0M/Qz87Ozs3OzczMzJKSkgAAAAAAAAAAAAAAAAAAAAAAAAAAACH5BAAHAP8ALAAAAAACABkAAAUYICCOImGeR6oqrMK88CM/UW1TeH5dGBYCADs=);
height:25px !important;
}

.left_pane_header {border-right: 1px solid #969696 !important; }

.left_pane_header ul, .right_pane_header ul {
line-height:19px !important;
}

.left_pane_header .tab_shadow {
background:url(data:image/png;base64,R0lGODlhAgAZAKIAAL+/v76+vr29vbu7u7i4uLW1tbCwsJKSkiH5BAAHAP8ALAAAAAACABkAAAMPaFZFNHCESau9eELH1DkJADs=) !important;
border:0 !important;
height:19px !important;
line-height:20px !important;
}

#left_pane, #right_pane {
margin-top:-10px !important;
}

.right_pane_header {border-left: 1px solid #969696 !important;}

.shadow {
    border:0 !important;
    padding-left:1px !important;
    border-right: 1px solid #969696 !important;
    border-bottom: 1px solid #969696 !important;
}



.left_pane_body {
    background-color:#dbdfe7 !important;
}

.right_pane_body, #note_contents
{
    background-color:#f0f0f0 !important;
    border:0 !important;
    border-left: 1px solid #969696 !important;
    border-bottom: 1px solid #969696 !important;
    padding:5px 12px 5px 5px !important;
}

.note_content {font-size:12px !important;}
#right_pane input, #right_pane textarea {
    background-color:#FFF !important;
    border:1px solid #969696 !important;
}

.note_item_selected .note_date {
    color:#fff !important;
}

.note_date {
    color:#000 !important;
}

.note_content a {
    color: #047df0 !important;
}

.menu_container img { display:none !important; }

}
