css:
body, table, p{font:10px Verdana;color:#EBEBEB}
a{font:8px Verdana;color:#99BEC4}
h3{font:bold 12px;color:#fff;padding-bottom:10px}

.menu li{list-style:none;float:left;width:94px}
.clock{position:absolute;top:0px;left:77px}

.menu_top{position:absolute;top:5px;left:270px}
.menu_top.right{position:absolute;top:5px;left:580px}

#left{width:435px;height:220px;border-right:1px solid #243948}
.wrap{position:relative;min-height:570px;clear:both}

#center{position:relative;float:right;width:562px;height:570px;border-right:1px solid #243948}
#center .content{width:610px;height:570px}

#right{width:435px;height:350px;border-right:1px solid #243948}
#right .content ul{padding-bottom:10px}

#left .content,#right .content{padding:20px 0px 0px 25px}
#left .content li,#right .content li{padding-bottom:7px}

.planet {width:550px}
.planet_p {width:450px}
.planet li{position:relative;float:left;width:80px;margin-bottom:8px;}
.queue{margin-left:90px;font:italic 10px}

#footer{padding: 5px 0px 5px 0px}
.footer_header a{font-size:10px;color:#fff}
.footer_header{font-size:10px}
.footer_left p,.footer_center p,.footer_right p{display:none}

.reddd{color:#f00;padding-left:23px}
#general a,#geo td a.small,.planet_name a{font:12px Verdana}
#geo td.right a.small,a.m_link,a.c_link{font:10px Verdana}

.planet_name a,#general a,a.small,a.m_link,a.c_link{color:aqua}
.planet_name a:link,#general a:link,a.small:link,a.m_link:link,a.c_link:link{color:aqua}

.planet_name a:visited,#general a:visited,a.small:visited,a.m_link:visited,a.c_link:visited{color:aqua}
.planet_name a:hover,#general a:hover,a.small:hover,a.m_link:hover,a.c_link:hover{color:white;text-decoration:underline}

input{color:#333;background-color:#f0f0f0}
input[type='button'],input[type='submit']{color:aqua;background-color:#223344;border-color:#04283f;padding:2px};
