css:
@-moz-document domain(boardgamearena.com)
{
#left-side{
	margin-right: 0px;   
}

#right-side {
    z-index:100000;
 	position: fixed;
    display: block;
 	right: 0;   
}
#right-side:hover {
background-color: lightgray;
padding-left: 8px;
padding-bottom: 8px;
padding-top: 8px;
border-radius: 16px 0 0 16px;
border-left: 2px solid black;
border-bottom: 2px solid black;
border-top: 2px solid black;
}
#right-side > * {
	display: none;
}
#right-side:hover > * {
    display: block;
}
#right-side:hover:before {
    display: none;
}
#right-side:before{
    content: '««««««««';
    text-orientation: upright;
    writing-mode: vertical-rl;
    position: absolute;
    right: 0;
    top: 0;
    z-index: 1000000;
    color: red;
    background-color: white;
    padding: 5px;
    font-weight: bold;
    background: linear-gradient(270deg,#f8f8f8,#e7e9e8);
    border-radius: 8px 0 0 8px;
}
}
