css:
@-moz-document url-prefix("https://web.roblox.com"), url-prefix("https://www.roblox.com")
{
/* i really want this to be global */
  .ad, .ad *, .img_ad, .ad-annotations, #google_image_div, #aw0, #AdvertisingLeaderboard
  {
    display: none !important;
  }

  .ad, .ad *, .img_ad, .ad-annotations, #google_image_div, #aw0, #AdvertisingLeaderboard
  {
    display: none !important;
  }
  /*Ad removal */
  #header
  {
    background-color: #001121;
  }

  .dark-theme .rbx-header
  {
    background-color: #ffffff00;
    border: solid #00000000;
    border-width: 0 0 1px;
  }

  .dialog-container .dialog-header

                             
  .navigation .notification-icon, .rbx-highlight
  {
    background: #191b1d;
  }

  #chat-header
  {
    background: #191b1d;
      /*Party header*/
    ;
  }

  body
  {
    background: transparent !important;
  }

  .content
  {
    background: transparent !important;
  }

  #fb-root
  {
    width: 100% !important;
    height: 100% !important;
    position: fixed !important;
    background-image: url(https://i.pinimg.com/originals/85/48/ea/8548ea0fa4ff626b5f50ca0ecc6afdb7.jpg) !important;
      /* Background */
    background-size: cover !important;
    background-attachment: fixed !important;
  }

  .avatar-back
  {
    background-image: url(https://i.pinimg.com/originals/85/48/ea/8548ea0fa4ff626b5f50ca0ecc6afdb7.jpg);
  }

  .dark-theme .section-content
  {
    background-color: #191b1ded;
    color: #fff;
  }

  .dark-theme .profile-avatar-right .profile-avatar-mask
  {
    background-color: #191b1d;
    color: #fff;
  }

  .dark-theme .avatar-container .avatar-card-image, .dark-theme .avatar-container img
  {
    background-color: #ffffff00;
  }

  .dark-theme .thumbnail-2d-container
  {
    border: 0;
    background-color: rgb(57 59 61 / 0%);
    border-color: #000000;
  }

  .dark-theme .avatar .avatar-card-image
  {
    background-color: #191b1d;
  }

  .dark-theme .rbx-tabs-horizontal .nav-tabs
  {
    background-color: #191b1d;
    color: #fff;
  }

  .dark-theme .profile-accoutrements-container .profile-accoutrements-slider .accoutrement-item a
  {
    background-color: #f8fefe0d;
  }

  .dark-theme .layer
  {
    background-color: #191b1d;
  }

  .dark-theme .text, .dark-theme .text-date-hint, .dark-theme .text-default, .dark-theme .text-description, .dark-theme .text-footer, .dark-theme .text-info, .dark-theme .text-label, .dark-theme .text-secondary, .dark-theme p
  {
    color: #f6fbff;
  }

  .dark-theme a.btn-common-play-game-lg
  {
    background-color: #1b5263;
    border-color: #000000;
    border-radius: 1px;
  }

  .dark-theme .btn-common-play-game-lg
  {
    background-color: #1b5263;
    border-color: #000000;
    color: #fff;
    border-radius: 8px;
  }

  .btn-common-play-game-lg, a.btn-common-play-game-lg
  {
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    background-color: #1b5263;
    border: 1px solid #000000;
    color: #fff;
    cursor: pointer;
    display: inline-block;
    font-weight: 500;
    height: auto;
    text-align: center;
    white-space: nowrap;
    vertical-align: middle;
    padding: 12px;
    font-size: 20px;
    line-height: 100%;
    border-radius: 5px;
  }

  .dark-theme .touch a.btn-common-play-game-lg.active, .dark-theme .touch a.btn-common-play-game-lg:active, .dark-theme .touch a.btn-common-play-game-lg:focus, .dark-theme .touch a.btn-common-play-game-lg:hover, .dark-theme .touch a.btn-common-play-game-lg:visited, .light-theme .touch a.btn-common-play-game-lg.active, .light-theme .touch a.btn-common-play-game-lg:active, .light-theme .touch a.btn-common-play-game-lg:focus, .light-theme .touch a.btn-common-play-game-lg:hover, .light-theme .touch a.btn-common-play-game-lg:visited
  {
    color: #fff;
    background-color: #001121;
    border-color: #f1fbfd;
  }

  .dark-theme .touch .btn-common-play-game-lg.active, .dark-theme .touch .btn-common-play-game-lg:active, .dark-theme .touch .btn-common-play-game-lg:focus, .dark-theme .touch .btn-common-play-game-lg:hover, .dark-theme .touch .btn-common-play-game-lg:visited, .light-theme .touch .btn-common-play-game-lg.active, .light-theme .touch .btn-common-play-game-lg:active, .light-theme .touch .btn-common-play-game-lg:focus, .light-theme .touch .btn-common-play-game-lg:hover, .light-theme .touch .btn-common-play-game-lg:visited, .light-theme a.btn-common-play-game-lg
  {
    color: #fff;
    background-color: #001121;
    border-color: #f1fbfd;
  }

  .dark-theme a.btn-common-play-game-lg:focus, .dark-theme a.btn-common-play-game-lg:hover
  {
    background-color: #001121;
    border-color: #f1fbfd;
    color: #fff;
  }

  .touch .btn-common-play-game-lg:focus, .touch .btn-common-play-game-lg:hover, .touch .btn-common-play-game-lg:visited, .touch a.btn-common-play-game-lg:focus, .touch a.btn-common-play-game-lg:hover, .touch a.btn-common-play-game-lg:visited
  {
    color: #fff;
    background-color: #001121;
    border-color: #f1fbfd;
  }

  .game-main-content .game-play-buttons .btn-common-play-game-lg
  {
    min-width: 100%;
    padding: 4px 16px;
  }

  .dark-theme .btn-common-play-game-lg:focus, .dark-theme .btn-common-play-game-lg:hover
  {
    background-color: #001121;
    border-color: #f1fbfd;
    color: #fff;
  }

  .dark-theme.btr-profile .btr-games-list .btr-game-button
  {
    background-color: #1c1e1f;
    border-color: #49494a;
  }
}
