css:
@-moz-document domain(boardgamearena.com)
{
#tableinfos > .table_ref_item {
 display: inline-block;   
    margin-right:20px;
}
#topbar {
    height: 32px;
margin-bottom:0;
}
#side-log img {
    height: 30px;
	width: 30px;
}
#page-title{
    font-size: 18px;
}
.no_time_limit #its_your_turn {
    font-size: 18px;
    position: relative;
    top: 4px;
}
.upperrightmenu_item .icon20, .upperrightmenu_item i {
    margin-top: 4px;
}
#page-title{
 	padding: 2px;   
}   
#logoiconimg, #logoiconimg_inprogress{
height: 30px !important;
width: 27px !important;
}
#reflexiontimevalues {
display: flex;
align-items: center;
justify-content: center;
}
#reflexiontimevalues > * {
    padding-left:10px;
    padding-right:10px;
}
#pagemaintitle_wrap, #page-title{
    display:flex !important;
    align-items: center; 
    justify-content: center;
}
#page-title > * {
    margin-left:25px;
    margin-right:25px;
}

#connected_user_avatar{
height: 35px;
width: 35px;
}
#toprightmenu {
    padding-top: 8px;
}
#menubar > .menu_item{
padding-top:8px
}
#advanced_replay_features{
    left: 0;
    position: absolute;
}

}
