css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.twilightheroes.com/header.php"){

a{color:#000!important}
}
@-moz-document url("http://www.twilightheroes.com/nav.php") {

*{color:#000!important}

}
@-moz-document domain("www.twilightheroes.com") {

body {
	color: #086!important;
	background-color: #044!important;
}

#wrapheader {
	background-image: none!important;}
#wrapnav {
	background-color: #00ECEC!important;
	border-color: #00B8C2!important;
}

#logodesc {
	background: #00DFE4!important;
	border-bottom: 1px solid #0087A7!important;
}
.forumrules {
	background-color: #00CC79!important;
	border-color: #009860!important;
}
h1 {
	color: #0ff!important;
}

.postapprove, .postapprove a:visited, .postapprove a:hover, .postapprove a:link, .postapprove a:active {
	color: #084!important;
}
.posthilit {
	background-color: #0f9!important;
}

.gen {
        color: #0ff!important;
}

.copyright {
	color: #044!important;
}
/* Tables
 ------------ */
th {
	color: #00A34F!important;
	background-color: #006699!important;
	background-image: none!important;
}

.tablebg {
	background-color: #00B8C2!important;
}

.catdiv,.rtl .catdiv {
	background: #069!important;
}

.cat {
	background-color: #00D0D7!important;
	background-image: none!important;
}

.row1 {
	background-color: #00ECEC!important;
}

.row2 {
	background-color: #00E1E5!important;
}

.row3 {
	background-color: #00C8D0!important;
}

.spacer {
	background-color: #D1D7DC!important;
}

hr {
	background-color: #00D7DC!important;
	color: #00D7DC!important;
}

/* Links
 ------------ */
a:link {
	color: #000!important;
}

a:active,
a:visited {
	color: #086!important;
}

a:hover {
	color: #069!important;
}

a.forumlink {
	color: #069!important;
}

a.topictitle:visited {
	color: #0B2!important;
}

th a,
th a:visited {
	color: #001985 !important;
}
input {
	color: #003333!important;
	border: 1px solid #00B8C2!important;
	background-color: #00FAFA!important;
}

textarea {
	background-color: #00FAFA!important;
	color: #003333!important;
	border: 1px solid #00B8C2!important;
}

select {
	color: #003333!important;
	background-color: #00FAFA!important;
	border: 1px solid #00B8C2!important;
}
.post {
	background-color: #069!important;
}

.btnbbcode {
	background-color: #00EFEF!important;
	border: 1px solid #006666!important;
}

.btnmain {
	background-color: #00ECEC!important;
	border: 1px solid #00B8C2!important;
}

.btnlite {
	background-color: #ECECEC!important;
	border: 1px solid #A9B8C2!important;
}

.btnfile {
	background-color: #00ECEC!important;
	border: 1px solid #00B8C2!important;
}

.helpline {
	background-color: #00E3E7!important;}
.quotetitle, .attachtitle {
	border-color: #00B8C2!important;
	color: #003333!important;
	background-color: #00B8C2!important;
}

.quotecontent, .attachcontent {
	border-color: #00B8C2!important;
	background-color: #00FAFA!important;
	color: #005C77!important;
}
.codetitle {
	border-color: #00B8C2!important;
	color: #003333!important;
	background-color: #00B8C2!important;
}

.codecontent {
	border-color: #00B8C2!important;
	background-color: #00FAFA!important;
}

.syntaxbg {
	color: #00FFFF!important;
}

.syntaxcomment {
	color: #00FF80!important;
}
.syntaxstring {
	color: #000DD0!important;
}


/* Private messages
 ------------------ */
.pm_marked_colour {
	background-color: #000000!important;
}

.pm_replied_colour {
	background-color: #00B8C2!important;
}

.pm_friend_colour {
	background-color: #007700!important;
}

.pm_foe_colour {
	background-color: #00DD00!important;
}
.sep {
	background-color: #00A34F!important;
}

h1 {
color:#050!important;
}
h2 {
color:#001337!important;
}}
@-moz-document url("http://www.twilightheroes.com/header.php"){

a{color:#000!important}
}
@-moz-document url("http://www.twilightheroes.com/nav.php") {

tr,td, td#bgcolor{background-color:#00b0b0!important}
*{color:#000!important}

}
