css:
@-moz-document domain("www.nitrotype.com")
{
  body
  {
    background-color: #000000;
    overflow-y: scroll;
    background: none;
    overflow-x: hidden;
    position: relative;
  }


  .card--grit
  {
    background-image: url("https://kpsu.org/wp-content/uploads/2017/10/city-street-night-wallpaper-3.jpg");
  }


  html
  {
    background: fixed url("https://thinkradius.com/wp-content/uploads/2014/05/city-street-night-wallpapers.jpg") no-repeat, #2d2e2e;
    box-sizing: border-box;
    color: #000000;
    font-family: "Montserrat", sans-serif;
    font-size: 16px;
    font-weight: 300;
    line-height: 1.6;
    background-size: 200%;
  }


  .bg--gradient
  {
    background: top left repeat, linear-gradient(90deg, #101010 60%, #1f2021 100%);
  }


  .card--a
  {
    background-image: url("https://wallpaperaccess.com/full/1989774.jpg");
    color: #fefeff;
    background-size: 100%;
  }


  .btn--primary
  {
    background: #000000;
    color: #f78e0a;
  }


  .theme--pDefault .profile-title
  {
    color: #000000;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
  }


  .theme--pDefault .profile-levelNum
  {
    color: #000000;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
  }


  .theme--pDefault .profile-body
  {
    color: #000000;
  }
}


@-moz-document domain(nitrotype.com)
{


  .dash-copyContainer
  {
    background: #000000;
    -webkit-box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    border-radius: 5px;
    flex: 1;
    overflow: hidden;
    padding: 15px;
    display: flex;
  }


  .dash-copy
  {
    color: #ffffff;
    font-family: "Roboto Mono", "Courier New", Courier, "Lucida Sans Typewriter", "Lucida Typewriter", monospace;
    font-size: 28px;
  }


  ::selection
  {
    background-color: #000000;
    color: white;
  }


  .bg--gradient
  {
    background: url(https://www.teahub.io/photos/full/314-3148419_wallpaper-night-city-street-city-lights-reflection-neon.jpg) no-repeat;
  }


  .theme--pDefault .profile-bg
  {
    background: url("https://www.teahub.io/photos/full/314-3148419_wallpaper-night-city-street-city-lights-reflection-neon.jpg") top left repeat, linear-gradient(0deg, #000000 60%, #ffffff 100%);
    color: #000000;
  }


  .btn--inky, .btn--inky:focus:not(:hover)
  {
    color: #000000;
  }


  .theme--pDefault .profile-bgHead
  {
    background: url("https://www.teahub.io/photos/full/314-3148419_wallpaper-night-city-street-city-lights-reflection-neon.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #000000 100%);
  }


  .theme--pDefault.is-gold .profile-bg
  {
    background: url("https://www.teahub.io/photos/full/314-3148419_wallpaper-night-city-street-city-lights-reflection-neon.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #ffffff 100%);
  }


  .theme--pDefault.is-guest .profile-bgHead
  {
    background: #000000;
  }
}
