css:
@-moz-document domain("www.nitrotype.com")
{
  body
  {
    background-color: #2d2e2e;
    overflow-y: scroll;
    background: none;
    overflow-x: hidden;
    position: relative;
  }


  .card--grit
  {
    background-image: url("http://chicagoareafire.com/blog/wp-content/uploads/2011/12/CFD_FH_E8-DMc.jpg");
  }


  html
  {
    background: fixed url("https://i.pinimg.com/736x/75/94/10/7594102b6431512bf234610c31b1ad0b.jpg") no-repeat, #2d2e2e;
    box-sizing: border-box;
    color: #2d2e2e;
    font-family: "Montserrat", sans-serif;
    font-size: 16px;
    font-weight: 300;
    line-height: 1.6;
    background-size: 200%;
  }


  .bg--gradient
  {
    background: top left repeat, linear-gradient(90deg, #101010 60%, #1f2021 100%);
  }


  .card--a
  {
    background-image: url("https://i.ytimg.com/vi/pFcDxUVPcx4/maxresdefault.jpg");
    color: #fefeff;
    background-size: 100%;
  }


  .btn--primary
  {
    background: #0c0c0c;
    color: #f78e0a;
  }


  .theme--pDefault .profile-title
  {
    color: #000000;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
  }


  .theme--pDefault .profile-levelNum
  {
    color: #000000;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
  }


  .theme--pDefault .profile-body
  {
    color: #000000;
  }
}


@-moz-document domain(nitrotype.com)
{


  .dash-copyContainer
  {
    background: #1d1e23;
    -webkit-box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    border-radius: 5px;
    flex: 1;
    overflow: hidden;
    padding: 15px;
    display: flex;
  }


  .dash-copy
  {
    color: #Color Id Of Race Text;
    font-family: "Roboto Mono", "Courier New", Courier, "Lucida Sans Typewriter", "Lucida Typewriter", monospace;
    font-size: 28px;
  }


  ::selection
  {
    background-color: #1976c6;
    color: white;
  }





  .theme--pDefault .profile-bg
  {
    background: url("https://www.recorder.com/getattachment/db5165ab-56eb-4da0-81ad-5942329dc7a7/a1-Greenfield-Fire-Department-holiday-light-di-(1)") top left repeat, linear-gradient(0deg, #2d2e2e 60%, #ffffff 100%);
    color: #d3d3d3;
  }


  .btn--inky, .btn--inky:focus:not(:hover)
  {
    color: #ffffff;
  }


  .theme--pDefault .profile-bgHead
  {
    background: url("https://www.recorder.com/getattachment/db5165ab-56eb-4da0-81ad-5942329dc7a7/a1-Greenfield-Fire-Department-holiday-light-di-(1)") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #ffffff 100%);
  }


  .theme--pDefault.is-gold .profile-bg
  {
    background: url("https://www.recorder.com/getattachment/db5165ab-56eb-4da0-81ad-5942329dc7a7/a1-Greenfield-Fire-Department-holiday-light-di-(1)") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #ffffff 100%);
  }


  .theme--pDefault.is-guest .profile-bgHead
  {
    background: #fff0;
  }
}
