css:
@-moz-document url-prefix("https://educonnect.education.gouv.fr"), domain("education.gouv.fr") {
.image-portail {
	text-align: left;
	background-repeat: no-repeat;
	background-size: 100% 100%;
	position: relative;
	display: block;
	background-color: #0f0f1a;
}

body{font-family:"Product Sans",Product Sans,Roboto,sans-serif;font-size:14px;line-height:1.42857143;color:#333;background-color: #0f0f14;}

.fr-card,
.fr-card.fr-enlarge-link {
  background-color: #1d1d2a;
  border-radius: 20px
  }

h1 {
  font-size: 1.5rem;
  line-height: 2rem;
  color: #e2e2e9;
  margin: 0;
  }

.sidebar {
  background: #1e1e2a;
  padding: 2.5rem;
  min-height: 50vh;
  margin: 0 0 2rem 0;
  border-radius: 20px
  }

.title-fs-18 {
  color: #e2e2e9;
  }

.fr-footer {
  color: var(--g700);
  border-top: 2px solid #000091;
  border-bottom: 0px solid #ceced9;
  background-color: #001;
  }

.fr-header {
  position: fixed;
  top: 0;
  z-index: 10000;
  border-bottom: 3px solid #000091;
  background-color: #001;
  height: 158px;
  }

.fr-header.minify .fr-header__logo img {
  height: 20px;
  background-color: #fff;
  }

.choixProfil {
  max-width: 588px;
  border-radius: 20px }
  .choixProfil__row {
    display: flex;
    justify-content: space-between; }
  .choixProfil__btn {
    position: relative;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: flex-end;
    width: calc(50% - 0.75rem);
    height: 233px;
    padding: 1.5rem;
    transition: all 0.3s ease;
    background-color: #1c1c2a;
    border-radius: 20px
    }
    .choixProfil__btn:hover {
      background-color: var(--g300); }
    .choixProfil__btn .fr-btn:before {
      content: "";
      position: absolute;
      top: 0px;
      left: 0px;
      bottom: 0;
      right: 0; }
  .choixProfil__icone {
    flex-grow: 1;
    display: flex;
    align-items: center;
    justify-content: center;
    width: 120px;
    height: 120px; }

.fr-btn {
    background-color: var(--w-bf500);
    color: var(--bf500);
    border-radius: 5px
}

.choixProfil__btn:hover {
    background-color: #101020;
}

.fr-header__service-title {
  font-size: 1.5rem;
  color: #fff;
  }

.fr-header.minify .fr-header__service-title {
  font-size: 1.125rem;
  color: #fff; }

.fr-link {
  color: #9a9aff;
  background-color: var(--t-plain);
  }

.fr-input {
    color: #eee;
    background-color: #3e3e4e;
    box-shadow: inset 0 -2px 0 0 #2e2e5e
}

* {
  outline: none;
  background-color: transparent;
  }

.fr-callout.success {
  background-color: #008941;
  --scheme-border: #008941;
  color: white;
  border-radius: 20px;
  }

.fr-footer__brand .fr-logo {
    font-size: 1.3125rem;
    color: #fff;
}

    .fr-footer__body--operator .fr-footer__content,
    .fr-footer__content {
        margin-top: 0;
        flex-basis: 50%;
        color: #eee;
    }

.fr-footer__content-link {
  font-weight: bold;
  color: #ccc;
  }

.fr-input.disabled {
    background: transparent;
    color: #e2e2e9;
    display: block;
}

h2 {
    margin-bottom: 1.5rem;
    font-size: 1.25rem;
    line-height: 1.75rem;
    color: #e2e2e9;
}


}
