css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("blog.fefe.de") {

html, body {color:#222;font-family: Georgia}
html {background-color:#aaa}
body {background-color:#fff;line-height:1.5em;margin: 0 auto;padding:1em 3em;width:35em}
h1,h2,h2 a,h3 {color:#900!important;font-family:Helvetica}
a:link {color:#900!important}
a:visited {color:#999!important}
a:hover {color:#666}
h2 a:visited{color:#900!important}
blockquote {color:#666}
ul {border-bottom: 1px solid #900;list-style-type:circle;margin:0;padding:0.5em 0;text-align:justify}
body > ul > li {border-bottom:1px solid #666;margin-top: 1em;padding-bottom: 1em}
li:last-child {border-bottom:0}
h3 + ul {padding-top:0}
blockquote ul {border-bottom:0}
body div:last-child {text-align:center!important;font-size:12px;}
}
