css:
@-moz-document domain(boardgamearena.com)
{


#discard_count {
	width: 150px;
	background-color: white;
	padding: 4px;
	border-radius: 4px;
	border: 2px solid black;
	display: flex;
	left: 10px;
	position: fixed;
	top: 100px;
	margin: 0;
	height: 16px;
	z-index: 1000;
}

#discard_count>#discard_count_label {
	font-weight: bold;
	flex: 1;
	text-align: right;
}

#sevenwonder_wrap #card_rotation_wrap {
	background-color: white;
	padding: 4px;
	border: 2px solid black;
	border-radius: 4px;
	display: flex;
	top: 135px;
	left: 10px;
	position: fixed;
	width: 150px;
	height: 20px;
	z-index: 1000;
}

#sevenwonder_wrap #card_rotation_wrap .rotate_icon {
	top: 0;
	display: block;
	margin-left: auto;
}

#sevenwonder_wrap #player_hand_wrap {
	background-color: rgba(255, 255, 255, 0.95);
	position: fixed;
	width: 100%;
	z-index: 100;
	bottom: 0;
	margin-bottom: 0px;
	margin-top: 0;
	border-top: 2px solid black;
	padding-bottom: 15px;
	padding-top: 20px;
}

#sevenwonder_wrap .cardmenu {
	top: 56px
}

#sevenwonder_wrap .cardmenuback {
	display: none
}

#sevenwonder_wrap .cardmenu>.menulinks {
	overflow: hidden;
	position: relative;
	margin-left: 30px;
	border-radius: 0 0 8px;
	border-radius: 0 0 12px 0;
	border-bottom: 1px solid black;
	height: 145px;
}

#sevenwonder_wrap .cardmenu .menulink {
	display: flex;
	flex-direction: row-reverse;
	width: 98px;
	height: 45px;
	padding-top: 3px;
	background-color: rgba(255, 255, 255, 0.7);
	cursor: pointer;
}

#sevenwonder_wrap .menulink:hover,
#sevenwonder_wrap .choosed .menulink {
	background-color: rgba(255, 255, 255, 1);
}

#sevenwonder_wrap .choosed .menuplay,
#sevenwonder_wrap .menulink:hover .menuplay {
	background-position: 0 -60px !important;
}

#sevenwonder_wrap .choosed .menuwonder,
#sevenwonder_wrap .menulink:hover .menuwonder {
	background-position: -60px -60px !important;
}

#sevenwonder_wrap .choosed .menudiscard,
#sevenwonder_wrap .menulink:hover .menudiscard {
	background-position: -120px -60px !important;
}

#sevenwonder_wrap .cardmenu .menulink:not(:last-child) {
	border-bottom: 2px solid rgba(0, 0, 0, 0.5);
}

#sevenwonder_wrap .cardmenu .menulink>.menuicon {
	position: relative;
	top: 0;
}

#sevenwonder_wrap .menuarrow:before {
	content: '☐';
	font-size: 32px;
	position: absolute;
	line-height: 1;
	border-radius: 2px;
	border-radius: 4px;
	height: auto;
	position: relative;
	color: #0077ed;
	font-weight: bold;
	color: rgba(0, 0, 0, 0.9);
}

#sevenwonder_wrap .menuarrow {
	background-image: none !important;
	width: auto;
	height: auto;
	position: relative;
	top: 0;
	left: 0;
	margin-top: 5px;
	margin-left: 0px;
	margin-right: 2px;
}

#sevenwonder_wrap .choosed .menuarrow:before {
	content: '☑';
}

#sevenwonder_wrap .choosed .menuarrow:before {}

#sevenwonder_wrap :not(.menulink)>.card_status {
	display: flex;
	justify-content: center;
	z-index: 1;
	position: relative;
	top: 0;
	left: 0;
	right: 0;
}

#sevenwonder_wrap #player_hand :not(.menulink)>.card_status {
	position: absolute !important;
	top: 37px;
	left: -1px;
}

#sevenwonder_wrap #player_hand .card_status>* {
	position: absolute;
	left: 0;
}

#sevenwonder_wrap .board_wonder_step .card_status_icon {
	position: absolute;
}

#sevenwonder_wrap #player_hand .card_status .card_status_nbr {
	width: 35px;
	text-align: center;
	margin-top: 1px;
}

#sevenwonder_wrap .card_status .card_status_nbr {
	width: 100%;
	text-align: center;
	z-index: 1000;
	margin-top: 1px;
	position: initial;
	left: 0;
}

#sevenwonder_wrap .couldplay {
	background-image: none;
}

#sevenwonder_wrap :not(#player_hand) .card_status_nbr {}

#sevenwonder_wrap #player_hand .card_status_nbr {}

#sevenwonder_wrap .card_status_nbr,
.tcoin {
	color: #FACE2B !important;
	top: 0;
	font-size: 26px !important;
	text-shadow: 1px 1px 1px #000, -1px -1px 1px #000, 1px -1px 1px #000, -1px 1px 1px #000, 1px 1px 1px #000;
}

#sevenwonder_wrap .cardcontent>.card_status {
	margin-top: -38px;
}

#sevenwonder_wrap .board_wonder_step .card_status {
	margin-top: -30px;
}

#sevenwonder_wrap #boardspaces>.boardspace~.whiteblock:nth-of-type(8) .card_status_icon,
#sevenwonder_wrap :not(.menulink)>.cardcontent>.card_status>*:first-child {
	background-color: white;
	border: 2px solid black;
	border-radius: 6px;
}

#sevenwonder_wrap .menulink .card_status {
	top: 0;
	left: 0;
}

#sevenwonder_wrap .tcoin {
	background-color: #f3eb0d;
	border: 1px solid black;
	border-radius: 100px;
	padding: 4px;
}

#sevenwonder_wrap .sicon_coin {
	display: none;
}

#sevenwonder_wrap span.tcoin {
	z-index: 50;
	position: absolute;
	font-weight: bold;
	font-size: 16px;
	position: absolute !important;
	color: rgba(0, 0, 0, 0.6);
	color: #2e2e2e;
	width: 30px;
	left: -37px;
	text-align: center;
	top: 0 !important;
	font-size: 19px;
}

#sevenwonder_wrap #paymentpossibilities_wrap {
	height: auto;
	position: fixed;
	width: 100%;
	z-index: 1200;
	background-color: white;
	bottom: 237px;
	border-top: 2px solid black;
}

#sevenwonder_wrap #boardspaces>.boardspace~.whiteblock:nth-of-type(8) {
	background-color: rgba(255, 255, 255, 0.9);
	opacity: 1;
}

#sevenwonder_wrap .whiteblock {
	opacity: 1 !important;
	background-image: none !important;
	background-color: rgba(255, 255, 255, 0.5);
	border-radius: 8px;
	opacity: 0.8;
}

#sevenwonder_wrap .player_board_wrap {
	display: flex !important;
	flex-flow: row wrap;
	justify-content: space-between;
	padding: 10px 10px 0 10px;
}

#sevenwonder_wrap .player_board_wrap h3 {
	position: absolute;
	top: 0;
	left: 0;
	margin: 0;
	background-color: lightgrey;
	padding: 2px;
	border-radius: 4px;
	writing-mode: vertical-rl;
	border: 1px solid black;
	z-index: 20;
}

#sevenwonder_wrap .player_board_name {
	top: auto;
}

#sevenwonder_wrap .board_resource {
	margin-bottom: 5px;
}

#sevenwonder_wrap #trees>h3 {
	display: none;
}

#sevenwonder_wrap #table_score_contents tr:first-child *:nth-child(2),
#sevenwonder_wrap #table_score_contents tr td:nth-child(2) {
	background-color: lightyellow;
}

#sevenwonder_wrap #table_score_contents tr:last-child th {
	background-color: yellow;
}

#sevenwonder_wrap #table_score_contents tr:last-child th:nth-child(2) {
	border: 4px solid red;
}


}
