css:
/*    
---------------
Google News & Finance Dark and Colors (All Nations)
---------------
Custom userstyle for: 'news.google.com' and 'www.google.com/finance'
Author: Sifus Maximus                   
Last update: 8.3.201O
---------------
*/

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("https://news.google"),
		url-prefix("http://news.google"),
		url-prefix("http://www.google.com/finance"),
		url-prefix("http://www.google.com.hk/finance"),
		url-prefix("http://www.google.ca/finance"),
		url-prefix("http://www.google.co.uk/finance"),
		url-prefix("http://fastflip.googlelabs.com")

{
/* -----Common settings for webpage----- */
body { background: black !important; }
a:link {text-decoration: none !important; 
	color: #BBB !important; }
a:hover {color: orange !important;
	text-decoration: none !important; }

/* -----Top Menu----- */
#gbar a {color: #AAA !important}
.gb2 {background: #111 !important}
#personalize-link {color: #acb0ff !important}

/* -----image Google News----- */
.search img, #title_bar img, img[alt="Go to Google News Home"] {width: 0 !important;
		padding-left: 178px !important;
		height: 50px !important;
                background-image:url("data:image/gif;base64,") !important;
	border: none !important; }

/* ----- interactive buttons and fields ----- */
.searchField, .local-input {
	-moz-appearance: none !important;
	background: #001111 !important;
	border: 1px solid #003333 !important;
	width: 380px !important;
	color: #c9e0ff !important;}

input[type="submit"],div.fewer-link, div.more-link {
	-moz-appearance: none !important;
	background: #001111 !important;
	border: 1px solid #003333 !important;
	width: auto !important;
	color: #c9e0ff !important;}

input[type="submit"]:hover ,div.fewer-link:hover, div.more-link:hover{
	-moz-appearance: none !important;
	background: #002222 !important;
	border: 1px solid #004444 !important;
	width: auto !important;
	color: orange !important;}

div.fewer-link a, div.more-link a{
	font-weight: normal !important;
	color: inherit !important; }

/* -----News Navigation in top left column----- */
select {-moz-appearance: none !important;
	background: #001111 !important;
	border: 1px solid #004444 !important;
	color: #c9e0ff !important;}

#left-nav-sections { background: black !important; border-color: black !important; }
td.nav div.title, div.links {font-family: Arial, sans-serif !important;
	font-size: 1em !important;
	color: #c9e0ff !important; }
.nub-item {border-color: #111 !important; }

.sel {color: #c9e0ff !important; }

/* -----News section headings e.g. World, Health----- */
.blended-section2 {overflow: visible !important;}
.basic-title {background: #aab8d5 !important;
	border-width: 4px !important; }
.text a {color: black !important; }
.text a:hover {color: red !important; }

.bar {height: 4px !important }
.b-w, .sp-w .basic-title {background: #fffd7e !important; }
.b-n, .sp-n .basic-title {background: #aaaae1 !important; }
.b-b, .sp-b .basic-title {background: #a0ffa0 !important; }
.b-t, .sp-t .basic-title {background: #ea8888 !important; }
.b-e, .sp-e .basic-title {background: #c398ec !important; }
.b-s, .sp-s .basic-title {background: #ffb686 !important; }
.b-l, .sp-l .basic-title {background: #97cce7 !important; }
.b-h, .sp-h .basic-title {background: #acb0ff !important; }
.b-m, .sp-m .basic-title {background: #9ccdcc !important; }
.b-ir, .sp-ir .basic-title {background: #94ffd0 !important; }
.b-af, .sp-af .basic-title {background: #caf8f0 !important; }
.b-se, .sp-se .basic-title {background: #cdece6 !important; }
.b-awkl, .sp-awkl .basic-title {background: #9191fb !important; }
.b-po, .sp-po .basic-title, #rpromo_h {background: #fda0bc !important; }
.b-po, .sp-po .basic-title, #lpromo_h {background: #9ae6ff !important; }

/* -----China specific----- */
.b-i, .sp-i .basic-title {background: #c4c4c4 !important; }
.b-y, .sp-y .basic-title {background: #ffc790 !important; }
.b-a, .sp-a .basic-title {background: #65c4e3 !important; }
.b-es, .sp-es .basic-title {background: #9ddcdb !important; }
.b-ed, .sp-ed .basic-title {background: #8686f3 !important; }
/* -----end of China specific----- */

.section-title a {color: black !important; }
.yesscript a {color: black !important; }
.results-stats .sel {color: black !important; }

h2.text  {font-family: Georgia, serif !important;
	font-size: 1.1em !important;
	font-weight: bold !important;
	line-height: 1.1 !important; }

.contents {color: #c9e0ff !important; }
.contents a:link {color: #578eff !important; }
.contents a:hover {color: orange !important; border: 2px red !important}

/* -----Stories----- */
div.snippet {font-family: Arial, sans-serif !important;
	font-size: 1em !important;
	line-height: 1.1 !important;
        height: auto !important;
	border-left: 3px solid #6e83a2 !important;
	margin: 5px 0  !important;
	padding-left: 6px !important;
	color: #d0dfff; }

h2.title, div.title {font-family: Georgia, serif !important;
	font-size: 1.1em !important;
	line-height: 1.1 !important;
	margin-left: 0 !important;
	margin-bottom: 0.1em !important;
	color: #AAA; }

 
/* -----developing stories main page top right column----- */
div.title {font-size: 1em !important; }

h2.title a, div.title a {color: #578eff !important;
	text-decoration: none !important;
	font-weight: bold !important; }

h2.title a:hover, div.title a:hover {color: orange !important; }
 
/* -----control story by-line below headline----- */
div.source,
div.sources,
div.sub-title,
.timestamp {
	font-size: 0.9em !important;
	font-weight: lighter !important;
	color: #666 !important;
	line-height: 1.5 !important;
	margin-left: 0 !important; }

div.source a,
div.sources a,
div.sub-title .author-link a {
	font-style: italic !important;
	color: #00e229 !important;
	text-decoration: none !important; }

div.source a:hover,
div.sub-title a:hover { color: #AA2F54 !important; }

/* -----headlines below story text -- including video links----- */
div.aa-inner a:link { color: #69a1e8 !important; }
div.aa-inner a:hover { color: orange !important; }

/* -----news outlet links below story text-----  */
span.source-link a:hover { color: red !important; }
  
/* -----"E-mail this story" link----- */
a.email-link { display: none !important; }

/* -----control link to all news stories below story text----- */
div.sources .more-coverage-text,
.more-coverage-text {
	font-size: 1em !important;
	font-style: italic !important;
	font-weight: lighter !important;
	text-transform: lowercase !important;
	color: #008000 !important;
	margin-left: 0 !important; }

a.more-coverage-text:link {color: #00e229 !important; }
a.more-coverage-text:hover {color: red !important; }

/* -----control story thumnail images----- */
div.thumbnail img {border: 1px solid #808080 !important; }
div.thumbnail div.source {display: none !important; }
.story {border: 0px !important; background-color: inherit !important; }


/* -----adjustment for single topic news pages----- */
div.thumbnail { float: right !important; }

/* -----fastflip section----- */
#fastflip-iframe {border: 1px red !important; }

.tabs {	background: #001111 !important;
	border: 1px solid #003333 !important;
	color: #c9e0ff !important;}

.tabs:hover {background: #002222 !important;
	border: 1px solid #004444 !important;
	width: auto !important;
	color: orange !important;}

.tabs .selected {background: black !important; 
	border-top: 1px solid #004444 !important;
	border-right: 1px solid #004444 !important;
	border-bottom: 1px solid black !important;
	border-left: 1px solid #004444 !important;
	color: orange !important;}

/* -----small image mosaic----- */
div.s-image-mosaic  div.story.image {
	overflow: hidden !important;
	width: 80px !important;
	height: 90px !important;
	margin-right: 2px !important; }

div.s-image-mosaic div.row {clear: both !important; padding-bottom: 4px !important; }
div.s-image-mosaic div.contents {padding-top: 5px !important; }
div.s-image-mosaic div.thumbnail {float: none !important; width: 80px !important; }
div.s-image-mosaic div.image {float: left !important; margin: 0 auto !important; }
div.s-image-mosaic .source {display: none !important; } 

/* ------End section----- */
.footer-disclaimer {color: #AAA !important; }
.bottom-search {background: #000909 !important; }
.links {color: yellow !important; }


/* -----GOOGLE FINANCE----- */
.sfe-logo img { width: 0 !important;
	padding-left: 178px !important;
	height: 50px !important;
	background-image:url("data:image/gif;base64,") !important}
#searchbox {
	-moz-appearance: none !important;
	background: #001111 !important;
	border: 1px solid #003333 !important;
	width: 380px !important;
	color: #c9e0ff !important;}
#ad {display: none !important;}

/* -----Common settings for webpage----- */
a, .g-section a {color: #578eff !important; }
a:hover, .g-section a:hover {color: orange !important; }
.g-section {background: black !important; color: #d0dfff !important; }
h3 {font-family: Georgia, serif !important;
	font-size: 1.1em !important;
	font-weight: bold !important;
	line-height: 1.1 !important;
	color: black !important; }
.src {color: grey !important; }
#n-r-:link {color: #00e229 !important; }
#n-r-:hover {color: red !important; }
.name #n-mn- {color: #578eff !important; font-weight: bold !important; }

.date {color: grey !important; }
.colHeader, .colHeader .grey, .colHeader .mktCap, .colHeader .volume
	{color: #97cce7 !important; 
	font-family: Georgia, serif !important;
	font-size: 1em !important;
	font-weight: bold !important;
	line-height: 1.1 !important;}

div.snippet {font-family: Arial, sans-serif !important;
	font-size: 1em !important;
	line-height: 1.1 !important;
        height: auto !important;
	border-left: 3px solid #6e83a2 !important;
	margin: 5px 0  !important;
	padding-left: 6px !important;
	color: #d0dfff; }

/* -----Left navigation menu----- */
#navmenu {border: 1px #578eff solid !important; }
.nav-item {background: #333 !important; }
.nav-item a {color: #c9e0ff !important; }
.nav-selected {color: #578eff !important;
	background: #c9e0ff !important;
	margin: 0px !important;}
.nav-expanded {background: #c9e0ff !important; }
.navsub {color: black !important; background: #c9e0ff !important; }
#nav-npf {color: black !important; font-style: italic !important; }
#nav-pf {color: black !important; font-style: italic !important; }

#rq-box, #rq-toggle {color: #c9e0ff !important;
	border: 1px #578eff solid !important;
	background: #333 !important; }
#rq-box h4 {color: #578eff !important;
            background: transparent !important; }
#rq-table a {color: yellow !important; }
#rq-create {color: orange !important; }

/* -----World Markets, Currencies, Bonds sections----- */
#markets .hdg, #currencies .hdg, #bonds .hdg
 { border-top: 4px solid #3c3ca4 !important; background: #aaaae1 !important; }
.symbol, .symbol a:link { color: #c9e0ff !important; }
.symbol a:hover { color: orange !important; }
.price {color: #578eff !important; }
.chg {color: #00ff24 !important; }
.chr {color: red !important; }
.chb {color: #c9e0ff !important; }

/* -----Main sections----- */
.top { border-top: 4px solid #578eff !important; background: #c9e0ff !important; }
.top .g-unit {background: #c9e0ff !important; }
#mk-news .hdg { border-top: 4px solid #ff6600 !important; background: #ffb686 !important; }
#country-widget .hdg { border-top: 4px solid green !important; background: #a0ffa0 !important; }
#trends { border-top: 4px solid #8960b2 !important; background: #c398ec !important; }

/* ----- Switches----- */
.goog-tab { background: inherit !important;
	border: none !important;
	font-family: Georgia, serif !important;
	font-size: 1em !important;
	font-weight: bold !important;
	line-height: 1 !important;}
.goog-tab a {color: black !important; }
.goog-tab-selected, .t { text-transform:uppercase !important;
	color: inherit !important;
	border: none !important;
	background: inherit !important; }


.mktCap {color: #578eff !important; }
.volume {color: #c9e0ff !important; }

/* -----StockScreener specific----- */
#add_criteria_wizard {background: #222 !important; }
.active, .criteriadiv, .criteria_list_div {background: #555 !important; color: red !important; }
.inactive {background: #444 !important; }

#searchresultssummary, #index-tabs {border-top: 4px solid green !important;
	background: #a0ffa0 !important;
	color: black !important;}
#searchresults td {color: inherit !important; background: #222 !important; }

/* -----GoogleDomesticTrends specific----- */
#compare-bar-wrapper {background: #222 !important; }

/* ------End section----- */
#footer {color: #BBB !important; background: #000909 !important; }
#footer a {color: #c9e0ff !important; }
#footer a:hover {color: orange !important; }
}
