css:
@-moz-document domain("ixbt.com") {
    .b-content {
        padding: 15px 10px;
    }

    .b-content--wrapper {
        width: auto !important;
    }

    .b-content .g-grid_column__big {
        width: 100%;
        padding: 0 10px;
        margin: 0;
    }

    .b-content .g-grid_column__small {
        display: none;
    }

    .b-block.block__newslistbig .item {
        display: block;
    }
}
