css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.wer-kennt-wen.de"){

DIV[style="position: relative;"]{ margin: 0px 0px 25px 300px !important;  }

#content{ width: 80% !important;font-weight: bold !important; font-size: 15px !important;}

TR{background: #f8f8ff !important;}
TH{ color: #000 !important; border: none !important; text-decoration: none !important;font-weight: bold !important;}

#footer > P{ display: none !important;}
#wrapper, #page,#shadow,#main, #seite{width: 1100px !important;}

#sidebar > DIV:first-child + UL + IMG + P,
#sidebar > DIV:first-child + UL + IMG + P + IMG + P,#ads {display: none !important;}

#sidebar >  H2 > SPAN,H2,
#content > P + H2 > SPAN{background: #778899 !important; color: #fff !important; border: none !important;font-weight: bold !important;}
#content > P + H2 + TABLE > TBODY > TR > TD > TABLE > TBODY > TR + TR > TD{ color: #770000 !important; text-decoration: none !important;font-weight: bold !important;}

#topright > P { color: #000 !important; font-weight: bold !important;font-family: verdana !important; font-size: 15px !important; }

#navigation > UL > LI{ margin: -1px 0px 25px 80px !important;background: transparent !important; color: #000 !important; border: none !important}
#navigation{background: transparent !important;  }
#navigation > UL > LI > A{ color: #000 !important; border: none !important;font-weight: bold !important; font-family: verdana !important; font-size: 15px !important;}
#navigation > UL > LI > A:hover { color: #ff0000 !important; border: none !important; text-decoration: none !important;}

INPUT,SELECT,TEXTAREA{font-weight: bold !important;background: #8b0000 !important; color: #fff !important;}
body {background: #000 !important;  border: none !important; width: 1400px !important;}

#shadow,#footer{background: #000 !important;  border: none !important;}
#main{background: #fff !important;  border: none !important; width: 130% !important;font-weight: bold !important;}
TD > A:hover{ color: #ff0000 !important; border: none !important; text-decoration: none !important;}

#rahmen > H1 + DIV + H2 > SPAN,
#rahmen > H1:first-child + DIV + H2 + P + TABLE + P + H2 > SPAN,
#rahmen > H1:first-child + DIV + H2 + P + TABLE + P + H2 + DIV + TABLE + BR + BR + DIV + H2 > SPAN,
#invitations > TBODY > TR:first-child > TH{background: #000 !important; color: #fff !important; border: none !important;}

.meta,.entry{  width: 1000px !important;}
.inviteForm{background: #000 !important; color: #fff !important; border: none !important;font-weight: bold !important;}
.submenu-item-link{background: transparent !important; color: #fff !important; margin: 0px 30px 0px 30px !important;}

#reiter > TABLE > TBODY > TR{background: transparent !important; color: #fff !important;}
#reiter {background: #000 !important; color: #fff !important; border: none !important;}

#addInvMessage{ color: #fff000 !important; border: none !important;}
#addInvMessage:hover{ color: #ff0000 !important; border: none !important; text-decoration: none !important;}

button,input[type="submit"],button:active,input[type="submit"]:active,.oneclick{background: #0000cd !important; color: #fff !important; text-decoration: none !important;}
button:hover,input[type="submit"]:hover,.oneclick:hover {background: #770000 !important; color: #fff !important; text-decoration: none !important;}

A:hover{ color: #ff0000 !important; border: none !important; text-decoration: none !important;}
A{ color: #0000cd !important; border: none !important; text-decoration: none !important;}

#rahmen > H1:first-child + DIV + H2 + P + TABLE + P + H2 + DIV + TABLE + BR + BR + DIV{ font-weight: bold !important; font-size: 28px !important;}
#peopleBar{ font-weight: bold !important; font-size: 18px !important;margin: -10px 0px 0px 220px !important;}}
