css:
@-moz-document url-prefix("https://jenkins.dev.interne.ocean.fr/")
{

:root {
  --breadcrumbs-bg: #111;
  --breadcrumbs-text-color: #efefef;
  --breadcrumbs-item-bg-color--hover: #ff6600;
}

.page-header {
    background: none;
    background-color: #FF6600 !important;
}

.logo a#jenkins-home-link::after {
    position: absolute;
    margin-top: 0;
    height: 100%;
    width: 86%;
    filter: invert(100%) grayscale(100%) drop-shadow(1px 2px 2px #424242);
    content: "" !important;
    background-image: url(https://fic.oceansystem.com/ocean.png) !important;
    background-size: cover;
}

#breadcrumbBar, #footer-container, .top-sticker-inner {
    background-color: #111;
    border-top: 1px solid var(--breadcrumbs-bg);
}

#breadcrumbBar .item, #footer-container .item, .top-sticker-inner .item {
    transition: all 500ms ease-in-out;
}
#breadcrumbBar .item:hover, #footer-container .item:hover, .top-sticker-inner .item:hover {
    transition: all 0ms linear;
}
.page-footer a:link {
    color: #efefef;
}

}
