/* ==UserStyle==
@name			DSM 7
@author			Domenico Blanco
@namespace		GitHub
@version		1.0.0
@preprocessor	default
==/UserStyle== */

@-moz-document regexp(".*:5000.*"){
    :root {
		color-scheme: dark;
		--primary-color: #18191a;
		--secondary-color: #242526;
		--third-color: #3a3b3c;
		--text: #057FEB;
	}
	
	#ext-gen489, .x-panel, .x-form-field-wrap, .x-grid3-header-bwrap, .x-grid3-header, .x-grid3, .x-grid3-hd, .x-menu, .x-menu-item, .x-tab-strip-wrap::before, .x-tab-strip-active::before, .x-toolbar, .syno-ux-button-default, .x-window-header, .x-window-tl, .syno-dl-tab-btn, .x-form-arrow-, .x-combo-list, .x-combo-list-item, .x-fieldset-header, .x-panel-footer, .x-window-body, .x-window-bl, .x-panel-fbar, .x-window-tbar, .syno-dl-leftpanel .syno-dl-tree .x-tree-node-expanded.x-tree-node-over, .syno-dl-leftpanel .syno-dl-tree .x-tree-node-collapsed.x-tree-node-over, .syno-ux-button-grey, .x-window-mc, .v-widget-item, .x-panel-body, .x-toolbar-ct, .x-panel-bbar, .x-menu-list, .x-menu-list-item, .sds-menu, .spotlight-main-window, .x-panel-body::before, .x-panel-body::after, .ux-pathtoolbar, .ux-pathbuttons-trigger, .tab-panel, .v-menu, .syno-ux-textarea.x-form-field, table.x-date-inner th, table.x-date-inner tbody tr td, li.x-tree-node-category, .v-fieldset-title-wrapper, .v-textfield-input, .v-btn-grey, .v-btn-cancel, .modal-win, .v-window-body, .v-select-dropdown, .body-wrapper, .fbar-wrapper, .x-combo-list-hd, .x-combo-list-inner, .ext-el-mask-msg, .x-loading-message-inner, .v-window-header-wrapper, .method-card-wrapper, .method-card {
		background: var(--primary-color)!important;
		background-color: var(--primary-color)!important;
		border-color: teal!important;
	}
	
	.x-menu-item::hover, .x-menu-item::focus, .syno-ux-button-default.x-item-disabled { 
		background: var(--secondary-color)!important;
		background-color: var(--secondary-color)!important;
	}
	
	.x-layout-split, .syno-pkglist-card:hover, .percentage-cmp-hbar-background, .usage-bar-bg {
		background-color: var(--third-color)!important;
		border-color: teal!important;
	}
	
	.x-form-arrow-trigger, .syno-ux-checkbox-icon, .x-form-text, .category-header, .x-form-trigger, .syno-ux-textfield > div, .syno-ux-triggerfield > div {
		background-color: var(--primary-color)!important;
		border-color: teal!important;
	}
	
	.x-menu-sep, .v-tabs-nav-wrap > div:before {
		background-color: teal!important;
	}
	
	.syno-pkglist-card {
		outline-color: teal;
	}
	
	.x-panel-header, .sds-user-menu-username {
		box-shadow: inset 0 -1px 0 0 teal!important;
	}
	
	.syno-dl-tab-selected {
		border-color: var(--text)!important;
	}
	
	.syno-ux-button button:first-child, .add-image, .syno-ux-triggerfield:before, .syno-ux-textfield:before, table.schedule-table:before, .v-fieldset-title-wrapper:before, .fbar-wrapper, .v-tabs-nav-wrap, .v-tabs-nav-wrap > div:before, .v-select-wrapper, .v-select-dropdown-divider, .v-textfield-textarea, .custom-logo__preview-logo, .preview-image, .x-form-text {
		border-color: teal!important;
	}
	
	table.schedule-table td {
		border-color: var(--primary-color)!important;
	}
	
	.syno-ux-button-grey {
		border-radius: 5px!important;
	}
	
	.x-window-header-text, span, td > div, .x-form-field-wrap input, .x-combo-list-item, .msg, .time, .input-container > input, div.username, .syno-ux-textarea.x-form-field, .syno-ux-textfield > div, table.schedule-table td, .v-textfield-input, .v-window-header-text {
		color: var(--text)!important;
	}
	
	.x-form-field-wrap input::placeholder, .x-btn-text, .tab-panel-title {
		color: teal!important;
	}	
}
