css:
@-moz-document url-prefix("https://linux.die.net/"), url-prefix("https://www.die.net/") {
html, body, head {
    background: #000;
    color: #bbb;
}

#logo, #menu {
    background: #000;
}

#menu dl {
    color: #ac480f;
}

h1 {
    border-bottom: 1px solid #ac480f;
    text-align: center;
    padding-bottom: 10px;
    padding-top: 10px;
    margin-bottom: 50px;
}

h2 {
    margin-top: 35px;
    border-left: 5px solid #ac480f;
    padding-left: 15px;
}


#menu input, #menu input:focus, input, input:focus {
    background: #333;
    color: #ddd;
    outline: none;
    border: none;
    padding-top: 5px;
    padding-bottom: 5px;
    margin-top: 10px;
    margin-bottom: 10px;
}

input:hover {
    background: #555;
}

a {
    color: #b38d02;
}

a:visited {
    color: #82580fbf;
}

#news {
    background: #111;
}
}
