css:
@-moz-document url-prefix(http://www.1up.com/boards/forums/show/) {


h4 a { 
color: #CC1100 !important; 
}

.blurb a:link, .blurb a:visited { color: #003366 !important;}

td.borderLeft a img, td.borderLeftLast a img { 
padding: 13px 16px 0 0;
height: 0px !important; 
width: 0px !important;
background-repeat: no-repeat;
margin: 0px 0px 0px 0px !important; 
}

img[src="/boards/templates/default/images/icon_folder_100.gif"]  { /*Icon_thread_new.gif*/
background-image: url(http://img80.imageshack.us/img80/3734/iconthreadnew.png) !important;
}

img[src="/boards/templates/default/images/icon_folder_201.gif"] { /*Icon_thread_new.gif*/
background-image: url(http://img80.imageshack.us/img80/3734/iconthreadnew.png) !important;
}

img[src="/boards/templates/default/images/icon_folder_0.gif"] { /*Icon_thread_new.gif*/
background-image: url(http://img218.imageshack.us/img218/8849/iconthreadnewhot.gif) !important; 
}

img[src="/boards/templates/default/images/icon_folder_200.gif"] { /*Icon_thread_new.gif*/
background-image: url(http://img218.imageshack.us/img218/8849/iconthreadnewhot.gif) !important; 
}

img[src="/boards/templates/default/images/icon_folder_210.gif"] { /*Icon_thread_new_hot_reply.gif*/
background-image: url(http://img413.imageshack.us/img413/9937/iconthreadnewhotreply.png) !important; 
}

img[src="/boards/templates/default/images/icon_folder_1.gif"] { /*Icon_thread_readonly_hot.gif*/
background-image: url(http://img718.imageshack.us/img718/9373/iconthreadreadonlyhot.png) !important; 
}

img[src="/boards/templates/default/images/icon_folder_101.gif"] { /*Icon_thread_readonly.gif*/
background-image: url(http://img33.imageshack.us/img33/5498/iconthreadreadonly.png) !important; 
}

img[src="/boards/templates/default/images/icon_folder_111.gif"] { /*Icon_thread_readonly_reply.gif*/
background-image: url(http://img404.imageshack.us/img404/9630/iconthreadreadonlyreply.png) !important; 
}

img[src="/boards/templates/default/images/icon_folder_211.gif"] { /*Icon_thread_readonly_hot_reply.gif*/
background-image: url(http://img10.imageshack.us/img10/521/iconthreadreadonlyhotre.png) !important; 
}

img[src="/boards/templates/default/images/icon_folder_110.gif"] { /*Icon_thread_new_reply.gif*/
background-image: url(http://img39.imageshack.us/img39/4766/iconthreadnewreply.png) !important;
}

tr td { padding: 0px !important}

th.forums_header_right { padding-left: 6px !important; }

th.forums_header_left { padding-right: 12px !important;}

td.borderLeft, td.borderLeftLast {
width: 0px !important;
padding: 0px 0px 0px 5px !important;
}

td.borderRight, td.borderRightLast {
width: 0px !important;
padding: 0px 0px 0px 0px !important;
}

td.borderLeft + td.borderCenter, td.borderLeftLast + td.borderCenterLast {
padding: 5px !important;
padding-top: 12px !important;
padding-bottom: 11px !important;
width: 1200px !important;
} 

td.aligncenter { padding: 5px !important; }

td {
background-image: none !important;
border-bottom: 1px solid #999999; 
border-right: 1px solid #999999; 
border-color: #999999 !important;
}

td.borderLeft + td.borderCenter, td.borderLeftLast + td.borderCenterLast { border-right: 0px; }

th {
/* padding: 10px !important; */
background-color: #f4f4f4 !important;
border-color: #999999 !important;
}

h4 { display: inline; font-size:small !important; font-weight:normal !important;}

td.borderRight, td.borderRightLast {
padding: 0px !important;
padding-left: 6px !important;
}

tr.sticky td {
/* padding: 10px !important; */
/* padding: 0px !important; */
background-color: #f2d7d5 !important;
background-color: #fdefee !important;
border-color: #e5b6b3 !important;
}

a[title="[ Latest Reply ]"] { font-size: 0px !important; }

a img[src="/boards/templates/default/images/folder_readall-mini.gif"] { 
padding: 13px 19px 0 0;
height: 0px !important; 
width: 0px !important;
background-repeat: no-repeat;
background-image: url(http://img704.imageshack.us/img704/1849/lithium3.png) !important;
margin: 0px 0px 0px 0px !important; 
}


span.blurb + span.blurb { font-size:0px !important;}

span.blurb + span.blurb a:link, span.blurb + span.blurb a:visited {
color: #424242 !important;
font-size: 10px !important;
}

span.blurb + span.blurb a:after { content: " ";}

span.blurb + span.blurb img:first-child { display:none !important; }

span.blurb + span.blurb a:nth-child(2):before { content: "| "; }
span.blurb + span.blurb a:nth-child(5):before { content: "..."; }


td.borderRight img, td.borderRightLast img { 
display:none !important;
}

div.body954  { 
width: 984px !important; 
margin: 0px 0px 0px 3px !important;
padding: 3px 0px 0px 0px !important;
}

div.body954 > div { 
/*prob fixed background-color: #D0D0D0 !important; */
background-color: #FFFFFF !important;
padding: 0px !important;
}

h2, .floatright, div.navTop { 
margin-left: 9px !important; 
margin-right: 9px !important;}

a[href="/boards/posts/insert/4.page"] { margin-left: 3px !important;  } 


}
