css:
/* Iconic Menubar - Ubuntu 
   by Plastikmaniac
   based on style #22367 "Iconic Menubar - KDE" by flying sheep 
*/

@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* icons */
#file-menu { 
  background-image: url("moz-icon://stock/document-open?size=menu") !important;}
#edit-menu {
  background-image: url("moz-icon://stock/text-editor?size=menu") !important;}
#view-menu {
  background-image: url("moz-icon://stock/eog?size=menu") !important;}
#history-menu {
  background-image: url("moz-icon://stock/appointment-soon?size=menu") !important;}
#bookmarksMenu {
  background-image: url("moz-icon://stock/user-bookmarks?size=menu") !important;}
#tools-menu {
  background-image: url("moz-icon://stock/preferences-system?size=menu") !important;}
#helpMenu {
  background-image: url("moz-icon://stock/help?size=menu") !important;}

/* icon extra */
#file-menu,
#edit-menu,
#view-menu,
#history-menu,
#bookmarksMenu,
#tools-menu,
#helpMenu {
	-moz-appearance: inherit !important;
	margin: 0px 2px 0px 2px !important;
	background-repeat: no-repeat !important;
	background-position: center !important;
}
#file-menu:hover, #edit-menu:hover, #view-menu:hover,
#history-menu:hover, #bookmarksMenu:hover,
#tools-menu:hover, #helpMenu:hover {
	-moz-border-radius: 3px !important;
}
#file-menu[open], #edit-menu[open], #view-menu[open],
#history-menu[open], #bookmarksMenu[open],
#tools-menu[open], #helpMenu[open] {
	-moz-border-radius-bottomleft: 0px !important;
	-moz-border-radius-bottomright: 0px !important;
}

/* hidden text */
#file-menu     .menubar-text,
#edit-menu     .menubar-text,
#view-menu     .menubar-text,
#history-menu  .menubar-text,
#bookmarksMenu .menubar-text,
#tools-menu    .menubar-text,
#helpMenu      .menubar-text {
	padding-left: 16px !important;
	width: 10px !important;
}
