==/UserStyle== */

@-moz-document domain("larucheenligne.net") {
 .training-path-container.js-path-container,
.modal-backdrop.show {
    display: none !important;
}


body.modal-open, body.custom-modal-open {
    overflow: visible !important;
}



.training-view-content-title {
    max-width: 640px;
    overflow: visible !important;
    word-wrap: anywhere;
    display: block;
    white-space: normal !important;
}



.training-view-footer{
    display: none !important;
}
.training-view .training-view-modules, .training-creation .training-view-modules {
    height: calc(100vh - 220px) !important;
}





#moduleContainer * {
    font-size: 18px !important;
}
body #textRender * {
    font-family: "Open Sans",sans-serif;
    font-weight: 500;
    font-size: 18px !important;
}

.widget-quiz .quiz-title p:first-child, .widget-survey .quiz-title p:first-child, .widget-scorm .quiz-title p:first-child {
    display: inline !important;
    color: #000 !important;
    font-weight: bold !important;
}
    

/*.training-view-left-container.anonymous-training-view-left-container,*/
body{
    background: #292929;
}


.breadcrumb-container .breadcrumb-content .breadcrumb .breadcrumb-item {
    color: #54b948;
}

.breadcrumb-container .breadcrumb-content .breadcrumb .breadcrumb-item {
    color: #45e132;
}

.breadcrumb-container .breadcrumb-content .breadcrumb .breadcrumb-item a {
    color: #99d591;
}

.left-trainings-filter-by.text-uppercase,
#dropdownMenuLink,
training-administration .trainings-filter-by {
    color: #FFF;
}

.btn-primary.inactive {
    background-image: unset;
    background-color: #99d591 !important;
    border: 2px solid #FFF;
    box-shadow: unset !important;
}
.btn-primary.inactive:hover {
    background-color: #45e132 !important;
    border: 2px solid #FFF!important;
    color: #FFF!important;
}

.my-trainings .divider {
    height: 1px;
    background-color: #47494b;
}

.footer {
    color: #54b948;
    background: #000;
}

#video-player {
    background: #828282;
}


.js-choices.list-group-item.js-multiple-choice-option {
    font-weight: bold !important;
}
.js-choices.list-group-item.js-multiple-choice-option:hover {
    background: #54b948;
    color: #000;
}

.js-choices.list-group-item.js-multiple-choice-option.is-selected {
    background: #f7cef0;
    color: #000;
}
.js-choices.list-group-item.js-multiple-choice-option.is-selected:hover {
    background: #f2b3e7;
    color: #000;
}


}
