css:
@-moz-document url-prefix("https://app.cvent.com") {
.cv-container {
    max-width: 1280px;
    margin: 0 auto;
}

.hold-conatiner-main {flex-direction: column;}

.fdn-nav-local-panel {
    width: 100% !important;
    display: flex;
    flex-direction: row;
    max-width: 100% !important;
    flex-wrap: wrap;
    justify-content: center;
    padding-top: .75em !important;
    padding-bottom: .75em !important;
    background: aliceblue !important;
    border-left: 1px solid #d9dcde;
    z-index: 10;
}

.fdn-nav-local-item.fdn-nav-local-item-primary.fdn-nav-has-secondary-menu {
    padding: .5em;
    font-size: 14px;
}
.fdn-nav-local-item-primary:hover, .fdn-nav-local-item-primary:focus {
    background: linear-gradient(to left, aliceblue 1.125em, mistyrose 1.125em);
}

#cv-left-nav a > div:first-child::after {
    content: "|";
    padding-left: .75em;
}
#cv-left-nav > li:last-child a > div:first-child::after {
    content: "";
    padding-left: 0;
}

#cv-left-nav .fdn-nav-i-arrow-chevron-down {display: none;}
.fdn-nav-local-secondary-menu.is-open {display: none !important;}
li[data-cvent-id^="cv-left-nav-primary-haschildren"]:hover > ul {
    position: absolute;
    background: aliceblue;
    border: 1px solid #d9dcde;
    margin-top: .325em;
    display:block !important;
}
li[data-cvent-id^="cv-left-nav-primary-haschildren"] > ul::before {
    border-left: 10px solid transparent;
    border-right: 10px solid transparent;
    border-bottom: 10px solid #d9dcde;
    content: "";
    position: absolute;
    top: -10px;
    left: .75em;
}
li[data-cvent-id^="cv-left-nav-primary-haschildren"] > ul::after {
    content: "";
    top: 0;
    position: absolute;
    left: 0;
    width: 100%;
    height: 10px;
    top: -10px;
}


.fdn-nav-local-secondary-menu li {margin-left: 0 !important;}
.fdn-nav-local-secondary-menu li:hover, .fdn-nav-local-secondary-menu li:focus {background-color: lavender !important;}

.hold-container-body {z-index:1}
}
