css:
/* i really want this to be global */

body, html {
    background-color: #232323;
}

p, a {
    color: white;
}

.main_menu a {
    color: white;
    border-bottom: 2px solid #FFFFFF;
}

#grey_board {
    background-color: #393939;
    border-radius: 10px;
}

.uppercase {
    color: white!important;
}

#grey_board a {
    color: white;
}

.data_news b {
    color: white;
}

li {
    color: white;
}

.footer_text {
    background-color: #393939;
}

.pager_main_top {
    display: none;
}

.mt_left_bb {
    padding: 0px 15px;
}

.tile_a {
    margin: 15px 7px 0 0;
}

.view_variant a.variant, .view_variant a.filter_a, .orange_button, .white_button {
    border: none;
}

.orange_button {
    color: #ffffff;
}

.mt_left, .mt_right {
    border: none;
}

#header {
    border-radius: 0px 0px 10px 10px;
}

.footer {
    background: none;
}

.rb_title {
    background-color: #603821;
}

#search_block input[type=submit] {
   margin: 8px 0px 10px 10px;
}

.mt_right div {
    color: white;
}

td {
    color: white;
}

.mymuseums td {
    background-color: #393939;
}

.mymuseums {
    border: none;
    border-radius: 10px!important;
}

.mymuseums td div {
    background-color: rgb(60 60 60 / 70);
}

.orange_button {
    box-shadow: 0px 3px 5px #a57c52;
}

.mymuseums input {
    cursor: pointer;
}

.content img {
    border-radius: 10px;
}
